import React, { useEffect, useState } from "react";
import { getProvider, getMyNFTs, getEthBalance } from "../contractUtils";

export default function Inventory({ user, search, setSearch }) {
  const [address, setAddress] = useState(null);
  const [cars, setCars] = useState([]);
  const [houses, setHouses] = useState([]);
  const [balance, setBalance] = useState("0");

  useEffect(() => {
    async function fetchData() {
      const provider = await getProvider();
      const signer = provider.getSigner();
      const userAddress = await signer.getAddress();
      setAddress(userAddress);
      const nfts = await getMyNFTs(userAddress);
      setCars(nfts.filter(nft => nft.itemType === "car"));
      setHouses(nfts.filter(nft => nft.itemType === "house"));
      if (
        userAddress &&
        typeof userAddress === "string" &&
        userAddress.startsWith("0x") &&
        userAddress.length === 42
      ) {
        setBalance(await getEthBalance(userAddress));
      }
    }
    fetchData();
  }, [user]);

  const s = (search || "").trim().toLowerCase();
  const filteredHouses = !s
    ? houses
    : houses.filter(
        nft =>
          nft.name.toLowerCase().includes(s) ||
          (nft.addr && nft.addr.toLowerCase().includes(s))
      );
  const filteredCars = !s
    ? cars
    : cars.filter(
        nft =>
          nft.name.toLowerCase().includes(s) ||
          (nft.vin && nft.vin.toLowerCase().includes(s))
      );

  return (
    <div className="inventory-bg">
      <div className="inventory-content">
        <div className="inventory-header">
          <h1 className="inventory-title">Your Properties</h1>
          <div className="inventory-balance-chip" title="Wallet balance">
            <span className="inventory-coin-emoji" role="img" aria-label="coin">ü™ô</span>
            <span className="inventory-balance-amount">
              {balance ? parseFloat(balance).toFixed(4) : "0.0000"}
            </span>
            <span className="inventory-balance-currency">ETH</span>
          </div>
        </div>
        <div className="inventory-desc">
          View your owned houses and cars below. Use the Transfer tab to send an item.
        </div>

        <div>
          <div className="section-title">Houses</div>
          <div className="card-grid">
            {filteredHouses.length === 0 && <span className="inventory-empty">No houses found.</span>}
            {filteredHouses.map((nft) => (
              <div key={nft.tokenId} className="asset-card">
                <div className="asset-type">üè†</div>
                <div className="asset-title">{nft.name}</div>
                <div className="asset-detail">Worth: <span className="asset-money">${nft.value}</span></div>
                <div className="asset-detail">Purchased: {nft.purchaseDate}</div>
                <div className="asset-detail">Address: {nft.addr}</div>
                <div className="asset-id">NFT ID: {nft.tokenId}</div>
              </div>
            ))}
          </div>
        </div>
        <div className="section-title" style={{marginTop:32}}>Cars</div>
        <div className="card-grid">
          {filteredCars.length === 0 && <span className="inventory-empty">No cars found.</span>}
          {filteredCars.map((nft) => (
            <div key={nft.tokenId} className="asset-card">
              <div className="asset-type">üöó</div>
              <div className="asset-title">{nft.name}</div>
              <div className="asset-detail">Worth: <span className="asset-money">${nft.value}</span></div>
              <div className="asset-detail">Purchased: {nft.purchaseDate}</div>
              <div className="asset-detail">VIN: {nft.vin}</div>
              <div className="asset-id">NFT ID: {nft.tokenId}</div>
            </div>
          ))}
        </div>
      </div>
      <style>{`
        @import url('https://fonts.googleapis.com/css2?family=Inter:wght@400;700;900&display=swap');
        .inventory-bg {
          min-height: 100vh;
          background: linear-gradient(110deg, #f6fafd 0%, #f4f6fb 90%);
          padding-bottom: 60px;
          font-family: 'Inter', system-ui, sans-serif;
        }
        .inventory-content {
          max-width: 1120px;
          margin: 0 auto;
          margin-top: 38px;
          padding: 0 18px 0 18px;
        }
        .inventory-header {
          display: flex;
          align-items: center;
          justify-content: space-between;
        }
        .inventory-title {
          font-size: 2.25rem;
          font-weight: 900;
          color: #20212a;
          margin-bottom: 8px;
          letter-spacing: 0.01em;
        }
        .inventory-balance-chip {
          display: flex;
          align-items: center;
          background: #fff;
          color: #181a23;
          border-radius: 25px;
          padding: 7px 19px 7px 14px;
          font-size: 1.11rem;
          font-weight: 700;
          border: 1px solid #ececf0;
          gap: 7px;
          min-width: 108px;
          margin-left: 16px;
          box-shadow: 0 1.5px 10px #e7eaf233;
        }
        .inventory-coin-emoji {
          font-size: 1.35em;
          margin-right: 2px;
          animation: coin-spin 1.3s linear infinite;
          filter: drop-shadow(0 1px 4px #0001);
          vertical-align: middle;
          display: inline-block;
          transform-origin: 60% 58%;
        }
        @keyframes coin-spin {
          0% { transform: rotateY(0deg);}
          65% { transform: rotateY(380deg);}
          100% { transform: rotateY(360deg);}
        }
        .inventory-balance-amount {
          font-weight: 900;
          letter-spacing: 0.01em;
        }
        .inventory-balance-currency {
          color: #8891b0;
          font-size: 0.98em;
          font-weight: 700;
          margin-left: 5px;
          letter-spacing: 0.03em;
        }
        .inventory-desc {
          margin-bottom: 28px;
          color: #555c6e;
          font-size: 1.14rem;
          font-weight: 400;
        }
        .section-title {
          font-size: 1.28rem;
          font-weight: 700;
          color: #1d2234;
          margin-bottom: 13px;
          letter-spacing: 0.01em;
        }
        .card-grid {
          display: grid;
          grid-template-columns: repeat(auto-fit, minmax(255px, 1fr));
          gap: 30px;
          margin-bottom: 36px;
        }
        .asset-card {
          background: #fff;
          border-radius: 18px;
          box-shadow: 0 3px 16px #e8eafd33, 0 1.5px 8px #c2c6e61b;
          padding: 23px 25px 18px 25px;
          display: flex;
          flex-direction: column;
          align-items: flex-start;
          min-width: 220px;
          transition: box-shadow .14s, border .17s, transform .13s;
          border: 2px solid transparent;
          position: relative;
          overflow: hidden;
        }
        .asset-card:hover {
          border: 2px solid #262cff;
          box-shadow: 0 6px 22px #aab8ff44, 0 1.5px 8px #c2c6e629;
          transform: translateY(-3px) scale(1.017);
        }
        .asset-type {
          font-size: 2.2rem;
          margin-bottom: 8px;
        }
        .asset-title {
          font-size: 1.14rem;
          font-weight: 700;
          color: #181a23;
          margin-bottom: 6px;
          letter-spacing: 0.03em;
        }
        .asset-detail {
          color: #596081;
          font-size: 1.03rem;
          margin-bottom: 2.5px;
        }
        .asset-money {
          color: #262cff;
          font-weight: 700;
          letter-spacing: 0.01em;
        }
        .asset-id {
          font-size: 0.97rem;
          color: #b0b2c3;
          margin-top: 6px;
          letter-spacing: 0.02em;
        }
        .inventory-empty {
          color: #bbbfd0;
          font-size: 1.04rem;
          font-weight: 500;
          padding: 15px 0 0 7px;
        }
        @media (max-width: 750px) {
          .card-grid {
            grid-template-columns: 1fr;
          }
          .inventory-content { padding: 0 2vw; }
        }
      `}</style>
    </div>
  );
}
