import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import { getProvider, getEthBalance } from "../contractUtils";

// Demo user mapping
const userMap = {
  "5000612430017": ["T12", "0xf39Fd6e51aad88F6F4ce6aB8827279cffFb92266"],
  "5000612430018": ["T13", "0x70997970C51812dc3A010C7d01b50e0d17dc79C8"],
  "5000612430019": ["T14", "0x3C44CdDdB6a900fa2b585dd299e03d12FA4293BC"],
};

export default function Login() {
  const [cnp, setCnp] = useState("");
  const [password, setPassword] = useState("");
  const [error, setError] = useState("");
  const [balance, setBalance] = useState(null);
  const [address, setAddress] = useState(null);
  const [loading, setLoading] = useState(false);
  const navigate = useNavigate();

  const handleMetaMask = async (e) => {
    e.preventDefault();
    setError("");
    setLoading(true);

    const entry = userMap[cnp];
    if (!entry || entry[0] !== password) {
      setError("Invalid CNP or password!");
      setLoading(false);
      return;
    }
    const expectedAddress = entry[1].toLowerCase();

    try {
      const provider = await getProvider();
      const signer = provider.getSigner();
      const userAddress = (await signer.getAddress()).toLowerCase();

      if (userAddress !== expectedAddress) {
        setError(
          `For this login, please select wallet address: ${expectedAddress} in MetaMask! (Currently selected: ${userAddress})`
        );
        setLoading(false);
        return;
      }

      setAddress(userAddress);
      setBalance(await getEthBalance(userAddress));
      setLoading(false);
      navigate("/inventory");
    } catch {
      setError("Please install MetaMask and connect to the Hardhat network.");
      setLoading(false);
    }
  };

  // For floating labels
  const labelClass = (val) => val ? "login-label floated" : "login-label";

  return (
    <div className="login-bg">
      <style>{`
        .login-bg {
          min-height: 100vh;
          background: linear-gradient(120deg, #f8fafc 0%, #e0e7ff 100%);
          display: flex;
          align-items: center;
          justify-content: center;
        }
        .login-card {
          background: #fff;
          padding: 38px 30px 26px 30px;
          border-radius: 22px;
          box-shadow: 0 8px 32px #0e174027;
          width: 100%;
          max-width: 410px;
          margin: 16px;
          position: relative;
          animation: fadeInUp 0.6s cubic-bezier(.23,1.01,.32,1);
        }
        @keyframes fadeInUp {
          from { opacity:0; transform:translateY(36px);}
          to   { opacity:1; transform:translateY(0);}
        }
        .login-title {
          text-align: center;
          font-size: 2rem;
          margin-bottom: 10px;
          font-weight: 700;
          letter-spacing: 0.5px;
          color: #2a4365;
        }
        .login-desc {
          color: #69718a;
          font-size: 1rem;
          text-align: center;
          margin-bottom: 26px;
        }
        .login-field {
          margin-bottom: 22px;
          position: relative;
        }
        .login-input {
          width: 100%;
          border: 1.5px solid #e2e8f0;
          border-radius: 8px;
          padding: 16px 12px 10px 12px;
          font-size: 1rem;
          outline: none;
          background: none;
          transition: border 0.18s;
        }
        .login-input:focus {
          border-color: #5c7cfa;
        }
        .login-label {
          position: absolute;
          left: 15px;
          top: 17px;
          color: #7c83a7;
          background: transparent;
          pointer-events: none;
          font-size: 1rem;
          transition: all 0.17s;
        }
        .floated {
          top: -10px;
          left: 9px;
          background: #fff;
          font-size: 0.87rem;
          padding: 0 3px;
          color: #4f46e5;
          font-weight: 500;
          letter-spacing: .2px;
        }
        .login-btn {
          margin-top: 10px;
          width: 100%;
          padding: 13px 0;
          font-size: 1.06rem;
          font-weight: 700;
          border-radius: 9px;
          background: linear-gradient(90deg, #4f46e5 0%, #5eead4 100%);
          color: #fff;
          border: none;
          box-shadow: 0 3px 10px #5c7cfa23;
          cursor: pointer;
          transition: background 0.17s, transform 0.11s;
        }
        .login-btn:hover {
          background: linear-gradient(92deg, #4338ca 0%, #0ea5e9 100%);
          transform: translateY(-2px) scale(1.025);
        }
        .login-error {
          margin-top: 13px;
          color: #e3342f;
          text-align: center;
          font-weight: 600;
          background: #fff5f5;
          border-radius: 6px;
          padding: 8px 0;
          animation: errorPop .27s cubic-bezier(.21,1.04,.38,1);
        }
        @keyframes errorPop {
          from { opacity:0; transform:scale(0.98);}
          to   { opacity:1; transform:scale(1);}
        }
        .switch-btn {
          background: #f7fafc;
          color: #4338ca;
          border: 1px solid #ddd;
          border-radius: 6px;
          padding: 7px 16px;
          margin-top: 10px;
          cursor: pointer;
          font-weight: 600;
          font-size: 1rem;
          transition: background 0.16s;
        }
        .switch-btn:hover {
          background: #e0e7ff;
        }
        .login-footer {
          display: flex;
          justify-content: center;
          gap: 32px;
          margin-top: 40px;
          color: #69718a;
          font-size: 0.98rem;
          opacity: 0.8;
        }
        .login-spinner {
          display: flex;
          align-items: center;
          justify-content: center;
          margin: 17px 0 10px 0;
        }
        .spinner {
          border: 3px solid #e0e7ff;
          border-top: 3px solid #4f46e5;
          border-radius: 50%;
          width: 32px;
          height: 32px;
          animation: spin 0.7s linear infinite;
        }
        @keyframes spin {
          0% { transform: rotate(0deg);}
          100% { transform: rotate(360deg);}
        }
      `}</style>
      <div className="login-card">
        <div className="login-title">Virtual Wallet Login</div>
        <div className="login-desc">Enter your CNP and password, then connect your wallet.</div>
        <form onSubmit={handleMetaMask}>
          <div className="login-field">
            <input
              id="cnp"
              className="login-input"
              type="text"
              value={cnp}
              onChange={e => setCnp(e.target.value)}
              autoComplete="username"
            />
            <label className={labelClass(cnp)} htmlFor="cnp">CNP</label>
          </div>
          <div className="login-field">
            <input
              id="pw"
              className="login-input"
              type="password"
              value={password}
              onChange={e => setPassword(e.target.value)}
              autoComplete="current-password"
            />
            <label className={labelClass(password)} htmlFor="pw">Password</label>
          </div>
          <button className="login-btn" type="submit" disabled={loading}>
            {loading ? "Connecting..." : "Log in with MetaMask"}
          </button>
        </form>
        {loading && (
          <div className="login-spinner">
            <div className="spinner"></div>
          </div>
        )}
        {error && (
          <div className="login-error">
            {error}
            {error.includes("please select wallet address:") && (
              <div>
                <button
                  className="switch-btn"
                  onClick={async () => {
                    try {
                      await window.ethereum.request({ method: "eth_requestAccounts" });
                      window.location.reload();
                    } catch (e) {
                      alert("Could not open MetaMask! Please open it manually.");
                    }
                  }}
                >
                  Switch Account in MetaMask
                </button>
              </div>
            )}
          </div>
        )}
        <div className="login-footer">
          <span>Contact Us</span>
          <span>Help</span>
          <span>Terms of Service</span>
        </div>
      </div>
    </div>
  );
}
