import React, { useEffect, useState } from "react";
import { getProvider, getMyNFTs, getEthBalance } from "../contractUtils";

export default function Inventory({ user, search, setSearch }) {
  const [address, setAddress] = useState(null);
  const [cars, setCars] = useState([]);
  const [houses, setHouses] = useState([]);
  const [balance, setBalance] = useState("0");

  useEffect(() => {
    async function fetchData() {
      const provider = await getProvider();
      const signer = provider.getSigner();
      const userAddress = await signer.getAddress();
      setAddress(userAddress);
      const nfts = await getMyNFTs(userAddress);
      setCars(nfts.filter(nft => nft.itemType === "car"));
      setHouses(nfts.filter(nft => nft.itemType === "house"));
      if (
        userAddress &&
        typeof userAddress === "string" &&
        userAddress.startsWith("0x") &&
        userAddress.length === 42
      ) {
        setBalance(await getEthBalance(userAddress));
      }
    }
    fetchData();
  }, [user]);

  // Filtering, derived from search
  const s = (search || "").trim().toLowerCase();
  const filteredHouses = !s
    ? houses
    : houses.filter(
        nft =>
          nft.name.toLowerCase().includes(s) ||
          (nft.addr && nft.addr.toLowerCase().includes(s))
      );
  const filteredCars = !s
    ? cars
    : cars.filter(
        nft =>
          nft.name.toLowerCase().includes(s) ||
          (nft.vin && nft.vin.toLowerCase().includes(s))
      );

  return (
    <div className="bg-casino">
      <div className="inventory-content-casino">
        <h1 className="casino-main-title">Your Properties</h1>
        <div className="inventory-desc-casino">
          View your owned houses and cars below. Use the Transfer tab to send an item.
        </div>
        <div>
          <div className="section-title-casino">Houses</div>
          <div className="card-grid-casino">
            {filteredHouses.length === 0 && <span className="casino-empty">No houses found.</span>}
            {filteredHouses.map((nft) => (
              <div key={nft.tokenId} className="asset-card-casino">
                <div className="asset-type-casino">🏠</div>
                <div className="asset-title-casino">{nft.name}</div>
                <div className="asset-detail-casino">Worth: <span className="casino-money">${nft.value}</span></div>
                <div className="asset-detail-casino">Purchased: {nft.purchaseDate}</div>
                <div className="asset-detail-casino">Address: {nft.addr}</div>
                <div className="asset-id-casino">NFT ID: {nft.tokenId}</div>
              </div>
            ))}
          </div>
        </div>
        <div className="section-title-casino" style={{marginTop:32}}>Cars</div>
        <div className="card-grid-casino">
          {filteredCars.length === 0 && <span className="casino-empty">No cars found.</span>}
          {filteredCars.map((nft) => (
            <div key={nft.tokenId} className="asset-card-casino">
              <div className="asset-type-casino">🚗</div>
              <div className="asset-title-casino">{nft.name}</div>
              <div className="asset-detail-casino">Worth: <span className="casino-money">${nft.value}</span></div>
              <div className="asset-detail-casino">Purchased: {nft.purchaseDate}</div>
              <div className="asset-detail-casino">VIN: {nft.vin}</div>
              <div className="asset-id-casino">NFT ID: {nft.tokenId}</div>
            </div>
          ))}
        </div>
      </div>
      <style>{`
        .bg-casino {
          min-height: 100vh;
          background: radial-gradient(ellipse at top right, #e0e7ff 0%, #f9fafc 80%);
          padding-bottom: 80px;
        }
        .inventory-content-casino {
          max-width: 1100px;
          margin: 0 auto;
          margin-top: 38px;
          padding: 0 18px 0 18px;
        }
        .casino-main-title {
          font-size: 2.25rem;
          font-weight: 900;
          margin-bottom: 9px;
          color: #23126d;
          letter-spacing: 0.01em;
          text-shadow: 0 4px 18px #90cdf427, 0 1px 0 #fff;
        }
        .inventory-desc-casino {
          margin-bottom: 32px;
          color: #5a6078;
          font-size: 1.16rem;
          font-weight: 400;
        }
        .section-title-casino {
          font-size: 1.35rem;
          font-weight: 700;
          color: #36326d;
          margin-bottom: 14px;
          letter-spacing: 0.02em;
          text-shadow: 0 1px 0 #fff;
        }
        .card-grid-casino {
          display: grid;
          grid-template-columns: repeat(auto-fit, minmax(265px, 1fr));
          gap: 30px;
          margin-bottom: 36px;
        }
        .asset-card-casino {
          background: rgba(255,255,255,0.88);
          border-radius: 19px;
          box-shadow: 0 6px 24px #6f68f13a, 0 1.5px 9px #0001;
          padding: 23px 26px 18px 26px;
          display: flex;
          flex-direction: column;
          align-items: flex-start;
          min-width: 235px;
          transition: box-shadow .17s, border .19s, transform .14s;
          border: 2.5px solid transparent;
          position: relative;
          overflow: hidden;
        }
        .asset-card-casino:hover {
          border: 2.5px solid #fbbf24;
          box-shadow: 0 12px 40px #ffe66a51, 0 2px 18px #6366f123;
          transform: translateY(-5px) scale(1.028);
        }
        .asset-type-casino {
          font-size: 2.35rem;
          margin-bottom: 8px;
          text-shadow: 0 2px 10px #facc1512;
        }
        .asset-title-casino {
          font-size: 1.18rem;
          font-weight: 700;
          color: #23126d;
          margin-bottom: 7px;
          letter-spacing: 0.03em;
          animation: shimmer-casino 2.3s infinite linear;
          background: linear-gradient(90deg,#ffe066,#6366f1 50%,#ffe066 100%);
          background-size: 200% 100%;
          -webkit-background-clip: text;
          -webkit-text-fill-color: transparent;
        }
        @keyframes shimmer-casino {
          0% { background-position: 120% 0; }
          100% { background-position: -120% 0; }
        }
        .asset-detail-casino {
          color: #69718a;
          font-size: 1.06rem;
          margin-bottom: 3px;
        }
        .casino-money {
          color: #fbbf24;
          font-weight: 800;
          letter-spacing: 0.02em;
        }
        .asset-id-casino {
          font-size: 0.99rem;
          color: #b0b2c3;
          margin-top: 7px;
          letter-spacing: 0.04em;
        }
        .casino-empty {
          color: #b1a7c7;
          font-size: 1.04rem;
          font-weight: 500;
          padding: 18px 0 0 7px;
        }
        @media (max-width: 750px) {
          .card-grid-casino {
            grid-template-columns: 1fr;
          }
          .inventory-content-casino { padding: 0 2vw; }
        }
      `}</style>
    </div>
  );
}
