import React, { useEffect, useState } from "react";
import { getProvider, getMyNFTs, getEthBalance } from "../contractUtils";
import { useNavigate } from "react-router-dom";

export default function Inventory() {
  const [address, setAddress] = useState(null);
  const [cars, setCars] = useState([]);
  const [houses, setHouses] = useState([]);
  const [balance, setBalance] = useState("0");
  const navigate = useNavigate();

  // Fetch address and NFTs only once, on mount
  useEffect(() => {
    async function fetchData() {
      const provider = await getProvider();
      const signer = provider.getSigner();
      const userAddress = await signer.getAddress();
      setAddress(userAddress);
      const nfts = await getMyNFTs(userAddress);
      setCars(nfts.filter(nft => nft.itemType === "car"));
      setHouses(nfts.filter(nft => nft.itemType === "house"));
    }
    fetchData();
  }, []);

  // Fetch balance only when address is valid, and set up interval only after address is set
  useEffect(() => {
    let interval;
    async function fetchBalance() {
      if (
        address &&
        typeof address === "string" &&
        address.startsWith("0x") &&
        address.length === 42
      ) {
        setBalance(await getEthBalance(address));
        // Now set up interval ONLY if address is valid
        interval = setInterval(async () => {
          setBalance(await getEthBalance(address));
        }, 10000);
      }
    }
    fetchBalance();
    return () => clearInterval(interval);
  }, [address]);

  return (
    <div>
      <div className="navbar">
        Virtual Wallet
        {address &&
        typeof address === "string" &&
        address.startsWith("0x") &&
        address.length === 42 ? (
          <span style={{ float: "right", fontWeight: "400", fontSize: "18px" }}>
            Balance: <span style={{ color: "#2a9d44" }}>{parseFloat(balance).toFixed(4)} ETH</span>
          </span>
        ) : null}
      </div>
      <div className="main-content">
        <h1 style={{ marginTop: 0, marginBottom: 28 }}>Your Properties</h1>
        <div style={{ marginBottom: 34, fontSize: 15, color: "#666" }}>
          View your owned houses and cars below. Use the Transfer tab to send an item.
        </div>
        <div>
          <div style={{ fontWeight: "bold", fontSize: 18, marginBottom: 10 }}>Houses</div>
          <div style={{ display: "flex", gap: 32, flexWrap: "wrap" }}>
            {houses.length === 0 && <span>No houses owned.</span>}
            {houses.map((nft) => (
              <div key={nft.tokenId} className="asset-card">
                <div className="asset-type">üè†</div>
                <div className="asset-title">{nft.name}</div>
                <div className="asset-detail">Worth: ${nft.value}</div>
                <div className="asset-detail">Purchased: {nft.purchaseDate}</div>
                <div className="asset-detail">Address: {nft.addr}</div>
                <div style={{ fontSize: 12, color: "#888" }}>NFT ID: {nft.tokenId}</div>
              </div>
            ))}
          </div>
        </div>
        <div style={{ fontWeight: "bold", fontSize: 18, margin: "30px 0 10px 0" }}>Cars</div>
        <div style={{ display: "flex", gap: 32, flexWrap: "wrap" }}>
          {cars.length === 0 && <span>No cars owned.</span>}
          {cars.map((nft) => (
            <div key={nft.tokenId} className="asset-card">
              <div className="asset-type">üöó</div>
              <div className="asset-title">{nft.name}</div>
              <div className="asset-detail">Worth: ${nft.value}</div>
              <div className="asset-detail">Purchased: {nft.purchaseDate}</div>
              <div className="asset-detail">VIN: {nft.vin}</div>
              <div style={{ fontSize: 12, color: "#888" }}>NFT ID: {nft.tokenId}</div>
            </div>
          ))}
        </div>
        <div style={{ marginTop: 36 }}>
          <button className="button-main" onClick={() => navigate("/transfer")}>
            Go to Transfer Page
          </button>
        </div>
      </div>
    </div>
  );
}
