import React, { useEffect, useState } from "react";
import {
  getProvider,
  getMyNFTs,
  createOffer,
  getPendingOffers,
  acceptOffer,
  getEthBalance,
} from "../contractUtils";

export default function Transfer({ user }) {
  const [address, setAddress] = useState(null);
  const [nfts, setNfts] = useState([]);
  const [selected, setSelected] = useState(null);
  const [to, setTo] = useState("");
  const [amount, setAmount] = useState("");
  const [message, setMessage] = useState("");
  const [offers, setOffers] = useState([]);
  const [balance, setBalance] = useState("0");
  const [loading, setLoading] = useState(false);

  useEffect(() => {
    async function fetchData() {
      const provider = await getProvider();
      const signer = provider.getSigner();
      const userAddress = await signer.getAddress();
      setAddress(userAddress);
      setNfts(await getMyNFTs(userAddress));
      setOffers(await getPendingOffers(userAddress));
      setBalance(await getEthBalance(userAddress));
    }
    fetchData();
  }, [user]);

  async function handleCreateOffer(e) {
    e.preventDefault();
    if (!selected || !to || !amount) {
      setMessage("Select an asset, recipient, and amount.");
      return;
    }
    try {
      setLoading(true);
      await createOffer(selected, to, amount);
      setMessage("Offer created! Buyer will see it in their pending offers.");
      setTo("");
      setAmount("");
      setSelected(null);
    } catch (err) {
      setMessage("Error: " + err.message);
    } finally {
      setLoading(false);
    }
  }

  async function handleAcceptOffer(offer) {
    try {
      setLoading(true);
      await acceptOffer(offer.offerId, offer.price);
      setMessage(
        `Offer accepted! You paid ${offer.price} ETH and received the item.`
      );
      setOffers(await getPendingOffers(address));
      setNfts(await getMyNFTs(address));
      setBalance(await getEthBalance(address));
    } catch (err) {
      setMessage("Error accepting offer: " + err.message);
    } finally {
      setLoading(false);
    }
  }

  return (
    <div className="page-bg">
      <div className="container">
        <h1 className="page-title">Send Trade Offer</h1>
        <div className="page-desc">
          Select an asset, enter recipient wallet, and set price (ETH) to create an offer.<br />
          Recipient must accept and pay for trade to complete!
        </div>
        <div className="asset-list">
          {nfts.map((nft) => (
            <div
              key={nft.tokenId}
              className={"asset-card" + (selected === nft.tokenId ? " selected" : "")}
              onClick={() => setSelected(nft.tokenId)}
              style={{ border: selected === nft.tokenId ? "2.5px solid #0052cc" : undefined, cursor: "pointer" }}
            >
              <div className="asset-type">
                {nft.itemType === "car" ? "🚗" : "🏠"}
              </div>
              <div className="asset-title">{nft.name}</div>
              <div className="asset-detail">
                {nft.itemType === "car"
                  ? `VIN: ${nft.vin}`
                  : `Address: ${nft.addr}`}
              </div>
              <div className="asset-id">
                NFT ID: {nft.tokenId}
              </div>
            </div>
          ))}
        </div>
        <form
          onSubmit={handleCreateOffer}
          className="transfer-form"
        >
          <label htmlFor="to" className="label">Recipient Wallet Address</label>
          <input
            id="to"
            className="input"
            type="text"
            placeholder="0x..."
            value={to}
            onChange={(e) => setTo(e.target.value)}
          />
          <label htmlFor="amount" className="label">Amount (ETH)</label>
          <input
            id="amount"
            className="input"
            type="number"
            step="0.0001"
            min="0"
            placeholder="Amount in ETH"
            value={amount}
            onChange={(e) => setAmount(e.target.value)}
          />
          <button className="button-main" type="submit" disabled={loading}>
            {loading ? "Processing..." : "Create Offer"}
          </button>
        </form>
        <div style={{ display: "flex", gap: 32, marginTop: 38 }}>
          <div style={{ flex: 1, maxWidth: 390 }}>
            <div className="section-title">Pending Offers To You</div>
            <div className="pending-offers-box">
              <div style={{ color: "#777", fontSize: 15, marginBottom: 10 }}>
                If someone offered you an asset for ETH, you'll see it here.
              </div>
              {offers.length === 0 && <span style={{ color: "#222", fontWeight: 500 }}>No pending offers.</span>}
              {offers.map((offer) => (
                <div key={offer.offerId} className="pending-offer-card">
                  <div className="pending-title">Offer for NFT #{offer.tokenId}</div>
                  <div className="pending-detail">
                    <span style={{ color: "#0052cc", fontWeight: 600 }}>Price:</span> {offer.price} ETH
                  </div>
                  <div style={{ fontSize: 13 }}>
                    From: <span style={{ color: "#555" }}>{offer.seller.slice(0, 8)}...{offer.seller.slice(-4)}</span>
                  </div>
                  <button
                    className="pending-btn"
                    onClick={() => handleAcceptOffer(offer)}
                    disabled={loading}
                  >
                    Accept & Pay
                  </button>
                </div>
              ))}
            </div>
          </div>
        </div>
        {message && (
          <div
            className={"transfer-message" + (message.startsWith("Error") ? " error" : "")}
          >
            {message}
          </div>
        )}
      </div>
      <style>{`
        .page-bg {
          min-height: 100vh;
          background: linear-gradient(120deg, #f8fafc 60%, #e0e7ff 100%);
          padding-bottom: 50px;
        }
        .container {
          max-width: 900px;
          margin: 44px auto 0 auto;
          padding: 0 16px;
        }
        .page-title {
          font-size: 2.1rem; font-weight: 800;
          margin-bottom: 6px; color: #1e293b;
        }
        .page-desc {
          margin-bottom: 32px;
          color: #64748b; font-size: 1.11rem;
        }
        .asset-list {
          display: flex; flex-wrap: wrap; gap: 26px; margin-bottom: 20px;
        }
        .asset-card {
          min-width: 220px; max-width: 260px;
          background: #fff;
          border-radius: 13px;
          box-shadow: 0 4px 24px #64748b10;
          padding: 18px 18px 13px 18px;
          display: flex; flex-direction: column; align-items: flex-start;
          margin-bottom: 13px;
          transition: box-shadow .17s, border .15s;
        }
        .asset-card.selected {
          border: 2.5px solid #0052cc !important;
          box-shadow: 0 4px 28px #0052cc14 !important;
          background: #f0f7ff !important;
        }
        .asset-type {
          font-size: 2.1rem; margin-bottom: 6px; opacity: 0.91;
        }
        .asset-title {
          font-size: 1.13rem; font-weight: 700;
          color: #3730a3; margin-bottom: 6px;
        }
        .asset-detail {
          color: #69718a; font-size: 1.02rem; margin-bottom: 2px;
        }
        .asset-id {
          font-size: 0.97rem; color: #b0b2c3; margin-top: 7px;
        }
        .transfer-form {
          margin: 0 auto;
          max-width: 350px;
          display: flex; flex-direction: column; gap: 10px;
          background: #fff;
          padding: 23px 24px 18px 24px;
          border-radius: 14px;
          box-shadow: 0 3px 18px #4f46e512;
        }
        .label {
          font-weight: 600; margin-bottom: 2px; color: #3730a3;
        }
        .input {
          padding: 8px 12px;
          border-radius: 9px;
          border: 1px solid #d1d5db;
          font-size: 1.09rem;
          margin-bottom: 7px;
        }
        .button-main {
          background: linear-gradient(90deg,#4f46e5,#06b6d4);
          color: #fff;
          border: none;
          font-weight: 700;
          padding: 12px 0;
          border-radius: 9px;
          font-size: 1.14rem;
          margin-top: 9px;
          box-shadow: 0 2px 9px #4f46e525;
          cursor: pointer;
          transition: background .18s,box-shadow .18s;
        }
        .button-main:hover {
          background: linear-gradient(90deg,#4f46e5,#22d3ee);
          box-shadow: 0 4px 20px #22d3ee22;
        }
        .pending-offers-box {
          background: #fff;
          border-radius: 16px;
          box-shadow: 0 8px 32px #4f46e509, 0 2px 16px #0001;
          padding: 18px 18px 15px 18px;
          min-height: 120px;
          margin-top: 5px;
        }
        .pending-offer-card {
          background: #f7fcf8;
          border-radius: 14px;
          padding: 18px 18px 15px 18px;
          margin-bottom: 13px;
