import React, { useEffect, useState } from "react";
import { getProvider, getMyNFTs, createOffer, getPendingOffers, acceptOffer, getEthBalance } from "../contractUtils";
import { useNavigate } from "react-router-dom";

export default function Transfer() {
  const [address, setAddress] = useState("");
  const [nfts, setNfts] = useState([]);
  const [selected, setSelected] = useState(null);
  const [to, setTo] = useState("");
  const [amount, setAmount] = useState(""); // ETH
  const [message, setMessage] = useState("");
  const [offers, setOffers] = useState([]);
  const [balance, setBalance] = useState("0");
  const navigate = useNavigate();

  // Fetch NFTs and ETH balance on mount
  useEffect(() => {
    async function fetchData() {
      const provider = await getProvider();
      const signer = provider.getSigner();
      const userAddress = await signer.getAddress();
      setAddress(userAddress);
      setNfts(await getMyNFTs(userAddress));
      setOffers(await getPendingOffers(userAddress));
      setBalance(await getEthBalance(userAddress));
    }
    fetchData();
    // Refresh balance every 10 seconds
    const interval = setInterval(async () => {
      if (address) setBalance(await getEthBalance(address));
    }, 10000);
    return () => clearInterval(interval);
  }, [address]);

  // Create offer (seller action)
  async function handleCreateOffer(e) {
    e.preventDefault();
    if (!selected || !to || !amount) {
      setMessage("Select an asset, recipient, and amount.");
      return;
    }
    try {
      await createOffer(selected, to, amount);
      setMessage("Offer created! Buyer will see it in their pending offers.");
      setTo(""); setAmount(""); setSelected(null);
    } catch (err) {
      setMessage("Error: " + err.message);
    }
  }

  // Accept offer (buyer action)
  async function handleAcceptOffer(offer) {
    try {
      await acceptOffer(offer.offerId, offer.price);
      setMessage(`Offer accepted! You paid ${offer.price} ETH and received the item.`);
      setOffers(await getPendingOffers(address));
      setNfts(await getMyNFTs(address));
      setBalance(await getEthBalance(address));
    } catch (err) {
      setMessage("Error accepting offer: " + err.message);
    }
  }

  return (
    <div>
      <div className="navbar">
        Virtual Wallet
        <span style={{float:"right",fontWeight:"400",fontSize:"18px"}}>Balance: <span style={{color:"#2a9d44"}}>{parseFloat(balance).toFixed(4)} ETH</span></span>
      </div>
      <div className="main-content">
        <h1 style={{marginTop:0}}>Send Trade Offer</h1>
        <div style={{marginBottom:24, color:"#666", fontSize:15}}>
          Select an asset, enter recipient wallet, and set price (ETH) to create an offer.<br />
          Recipient must accept and pay for trade to complete!
        </div>
        <div style={{display:"flex",gap:40,flexWrap:"wrap",marginBottom:32,justifyContent:"center"}}>
          {nfts.map(nft => (
            <div
              key={nft.tokenId}
              className="asset-card"
              onClick={() => setSelected(nft.tokenId)}
              style={{
                border: selected === nft.tokenId ? "2.5px solid #0052cc" : undefined,
                boxShadow: selected === nft.tokenId ? "0 4px 24px #0052cc14" : undefined,
                cursor: "pointer",
                background: selected === nft.tokenId ? "#f0f7ff" : undefined
              }}
            >
              <div className="asset-type">{nft.itemType === "car" ? "üöó" : "üè†"}</div>
              <div className="asset-title">{nft.name}</div>
              <div className="asset-detail">{nft.itemType === "car" ? `VIN: ${nft.vin}` : `Address: ${nft.addr}`}</div>
              <div style={{ fontSize: 12, color: "#888" }}>NFT ID: {nft.tokenId}</div>
            </div>
          ))}
        </div>
        <form onSubmit={handleCreateOffer} style={{margin:"0 auto",maxWidth:340,display:"flex",flexDirection:"column",gap:15}}>
          <label htmlFor="to">Recipient Wallet Address</label>
          <input
            id="to"
            type="text"
            placeholder="0x..."
            value={to}
            onChange={e => setTo(e.target.value)}
            style={{width:"100%"}}
          />
          <label htmlFor="amount">Amount (ETH)</label>
          <input
            id="amount"
            type="number"
            step="0.0001"
            min="0"
            placeholder="Amount in ETH"
            value={amount}
            onChange={e => setAmount(e.target.value)}
            style={{width:"100%"}}
          />
          <button className="button-main" type="submit">Create Offer</button>
        </form>

        <div style={{marginTop:48,marginBottom:8,fontWeight:600,fontSize:19}}>Pending Offers To You</div>
        <div style={{color:"#777",fontSize:15,marginBottom:14}}>If someone offered you an asset for ETH, you'll see it here.</div>
        <div style={{display:"flex",flexWrap:"wrap",gap:26}}>
          {offers.length === 0 && <span>No pending offers.</span>}
          {offers.map(offer => (
            <div key={offer.offerId} className="asset-card" style={{minWidth:300,background:"#f7fcf8"}}>
              <div className="asset-title" style={{marginBottom:10}}>Offer for NFT #{offer.tokenId}</div>
              <div style={{marginBottom:8}}><span style={{color:"#0052cc",fontWeight:600}}>Price:</span> {offer.price} ETH</div>
              <div style={{fontSize:13}}>From: <span style={{color:"#555"}}>{offer.seller.slice(0,8)}...{offer.seller.slice(-4)}</span></div>
              <button className="button-main" style={{marginTop:16}} onClick={() => handleAcceptOffer(offer)}>Accept & Pay</button>
            </div>
          ))}
        </div>

        {message && <div style={{marginTop:30,marginBottom:10,fontWeight:500,color: message.includes("success") || message.includes("created")?"#3a3":"#e44"}}>{message}</div>}

        <div style={{marginTop:44}}>
          <button className="button-main" onClick={() => navigate("/inventory")}>Back to Inventory</button>
        </div>
      </div>
    </div>
  );
}
