import React, { useEffect, useState } from "react";
import {
  getProvider,
  getMyNFTs,
  createBatchOffers,
} from "../contractUtils";

export default function Transfer({ user }) {
  const [address, setAddress] = useState(null);
  const [nfts, setNfts] = useState([]);
  const [selected, setSelected] = useState([]);
  const [to, setTo] = useState("");
  const [amount, setAmount] = useState("");
  const [message, setMessage] = useState("");
  const [loading, setLoading] = useState(false);

  useEffect(() => {
    async function fetchData() {
      const provider = await getProvider();
      const signer = provider.getSigner();
      const userAddress = await signer.getAddress();
      setAddress(userAddress);
      setNfts(await getMyNFTs(userAddress));
    }
    fetchData();
  }, [user]);

  function toggleSelected(tokenId) {
    setSelected((prev) =>
      prev.includes(tokenId)
        ? prev.filter((id) => id !== tokenId)
        : [...prev, tokenId]
    );
  }

  async function handleCreateOffer(e) {
    e.preventDefault();
    if (!selected.length || !to || !amount) {
      setMessage("Select one or more assets, recipient, and amount.");
      return;
    }
    setMessage("");
    setLoading(true);
    try {
      await createBatchOffers(selected, to, amount);
      setMessage(
        selected.length > 1
          ? `Offers created for ${selected.length} items!`
          : "Offer created!"
      );
      setTo("");
      setAmount("");
      setSelected([]);
    } catch (err) {
      setMessage("Error: " + (err?.message || err));
    } finally {
      setLoading(false);
    }
  }

  return (
    <div className="casino-transfer-bg">
      <div className="casino-transfer-container">
        <h1 className="casino-main-title">Send Trade Offer</h1>
        <div className="casino-transfer-desc">
          Select <b>one or more assets</b>, enter recipient wallet, and set price (ETH) to create offers.<br />
          Recipient must accept and pay for trade to complete!
        </div>
        <div className="casino-asset-list">
          {nfts.map((nft) => (
            <div
              key={nft.tokenId}
              className={
                "casino-asset-card" +
                (selected.includes(nft.tokenId) ? " casino-selected" : "")
              }
              onClick={() => toggleSelected(nft.tokenId)}
            >
              <div className="casino-asset-type">
                {nft.itemType === "car" ? "üöó" : "üè†"}
              </div>
              <div className="casino-asset-title animated-title">{nft.name}</div>
              <div className="casino-asset-detail">
                {nft.itemType === "car"
                  ? `VIN: ${nft.vin}`
                  : `Address: ${nft.addr}`}
              </div>
              <div className="casino-asset-id">NFT ID: {nft.tokenId}</div>
              {selected.includes(nft.tokenId) && (
                <span className="casino-multiselect-check">‚úì</span>
              )}
            </div>
          ))}
        </div>
        <form onSubmit={handleCreateOffer} className="casino-transfer-form">
          <label htmlFor="to" className="casino-label">
            Recipient Wallet Address
          </label>
          <input
            id="to"
            className="casino-input"
            type="text"
            placeholder="0x..."
            value={to}
            onChange={(e) => setTo(e.target.value)}
            disabled={loading}
          />
          <label htmlFor="amount" className="casino-label">
            Amount (ETH) <span style={{ color: "#aaa", fontSize: 13 }}>(for each item)</span>
          </label>
          <input
            id="amount"
            className="casino-input"
            type="number"
            step="0.0001"
            min="0"
            placeholder="Amount in ETH"
            value={amount}
            onChange={(e) => setAmount(e.target.value)}
            disabled={loading}
          />
          <button className="casino-button-main" type="submit" disabled={loading}>
            {loading
              ? `Processing${selected.length > 1 ? ` ${selected.length}` : ""}...`
              : selected.length > 1
                ? `Create ${selected.length} Offers`
                : "Create Offer"}
          </button>
        </form>
        {message && (
          <div
            className={
              "casino-transfer-message" +
              (message.startsWith("Error") ? " error" : "")
            }
          >
            {message}
          </div>
        )}
      </div>
      <style>{`
        /* Clean title shimmer animation */
        .casino-asset-title.animated-title {
          animation: shimmer-casino 2.3s infinite linear;
          background: linear-gradient(90deg,#ffe066,#6366f1 50%,#ffe066 100%);
          background-size: 200% 100%;
          -webkit-background-clip: text;
          -webkit-text-fill-color: transparent;
        }
        @keyframes shimmer-casino {
          0% { background-position: 120% 0; }
          100% { background-position: -120% 0; }
        }
        /* ...keep your previous styles unchanged... */
      `}</style>
    </div>
  );
}
