import React, { useState } from "react";
import { BrowserRouter as Router, Routes, Route, useNavigate, Navigate, useLocation } from "react-router-dom";
import Login from "./components/Login";
import Inventory from "./components/Inventory";
import Transfer from "./components/Transfer";
import About from "./components/About";
import Navbar from "./components/Navbar";

function MainAppWrapper() {
  const [showAccountModal, setShowAccountModal] = useState(false);
  const [walletAddress, setWalletAddress] = useState(""); // Set by Inventory/Transfer
  const navigate = useNavigate();

  const handleSearch = (query) => {
    navigate(`/inventory?search=${encodeURIComponent(query)}`);
  };

  const handleLogout = () => {
    navigate("/");
    // Add your state reset logic here if needed
  };

  return (
    <>
      <Navbar
        onSearch={handleSearch}
        walletAddress={walletAddress}
        onLogout={handleLogout}
        showAccountModal={showAccountModal}
        setShowAccountModal={setShowAccountModal}
      />
      <Routes>
        <Route path="/inventory" element={<Inventory setWalletAddress={setWalletAddress} />} />
        <Route path="/transfer" element={<Transfer setWalletAddress={setWalletAddress} />} />
        <Route path="/about" element={<About />} />
        <Route path="*" element={<Navigate to="/inventory" />} />
      </Routes>
    </>
  );
}

export default function App() {
  return (
    <Router>
      <Routes>
        <Route path="/" element={<Login />} />
        <Route path="/*" element={<MainAppWrapper />} />
      </Routes>
    </Router>
  );
}
