import React, { useEffect, useState } from "react";
import {
  getProvider,
  getMyNFTs,
  createOffer,
  getPendingOffers,
  acceptOffer,
  getEthBalance,
} from "../contractUtils";
import { useNavigate, useLocation } from "react-router-dom";

export default function Transfer() {
  const [address, setAddress] = useState(null);
  const [nfts, setNfts] = useState([]);
  const [selected, setSelected] = useState(null);
  const [to, setTo] = useState("");
  const [amount, setAmount] = useState("");
  const [message, setMessage] = useState("");
  const [offers, setOffers] = useState([]);
  const [balance, setBalance] = useState("0");
  const [loading, setLoading] = useState(false);
  const navigate = useNavigate();
  const location = useLocation();

  useEffect(() => {
    async function fetchData() {
      const provider = await getProvider();
      const signer = provider.getSigner();
      const userAddress = await signer.getAddress();
      setAddress(userAddress);
      setNfts(await getMyNFTs(userAddress));
      setOffers(await getPendingOffers(userAddress));
      if (
        userAddress &&
        typeof userAddress === "string" &&
        userAddress.startsWith("0x") &&
        userAddress.length === 42
      ) {
        setBalance(await getEthBalance(userAddress));
      }
    }
    fetchData();

    const interval = setInterval(async () => {
      if (
        address &&
        typeof address === "string" &&
        address.startsWith("0x") &&
        address.length === 42
      ) {
        setBalance(await getEthBalance(address));
      }
    }, 10000);
    return () => clearInterval(interval);
  }, [address]);

  function Tab({ label, to }) {
    const active = location.pathname === to;
    return (
      <div
        className={`tab-modern${active ? " tab-active" : ""}`}
        onClick={() => navigate(to)}
        tabIndex={0}
      >
        {label}
      </div>
    );
  }

  async function handleCreateOffer(e) {
    e.preventDefault();
    if (!selected || !to || !amount) {
      setMessage("Select an asset, recipient, and amount.");
      return;
    }
    try {
      setLoading(true);
      await createOffer(selected, to, amount);
      setMessage("Offer created! Buyer will see it in their pending offers.");
      setTo("");
      setAmount("");
      setSelected(null);
    } catch (err) {
      setMessage("Error: " + err.message);
    } finally {
      setLoading(false);
    }
  }

  async function handleAcceptOffer(offer) {
    try {
      setLoading(true);
      await acceptOffer(offer.offerId, offer.price);
      setMessage(
        `Offer accepted! You paid ${offer.price} ETH and received the item.`
      );
      setOffers(await getPendingOffers(address));
      setNfts(await getMyNFTs(address));
      if (
        address &&
        typeof address === "string" &&
        address.startsWith("0x") &&
        address.length === 42
      ) {
        setBalance(await getEthBalance(address));
      }
    } catch (err) {
      setMessage("Error accepting offer: " + err.message);
    } finally {
      setLoading(false);
    }
  }

  return (
    <div className="transfer-bg">
      <style>{`
        /* --- Navbar styles, tab styles etc (same as before) --- */
        .navbar-modern {
          width: 100%;
          background: linear-gradient(90deg, #4f46e5 0%, #5eead4 100%);
          color: #fff;
          padding: 16px 32px 0 32px;
          font-size: 1.3rem;
          font-weight: 700;
          border-radius: 0 0 18px 18px;
          display: flex;
          flex-direction: column;
          box-shadow: 0 3px 20px #0002;
        }
        .nav-top {
          display: flex;
          align-items: center;
          justify-content: space-between;
        }
        .balance-modern {
          font-size: 1.09rem;
          font-weight: 600;
          background: #fff;
          color: #22c55e;
          border-radius: 8px;
          padding: 3px 16px;
          margin-left: 20px;
        }
        .tab-bar {
          display: flex;
          gap: 8px;
          margin-top: 13px;
          margin-bottom: -4px;
          padding-left: 4px;
        }
        .tab-modern {
          padding: 7px 34px 8px 34px;
          background: transparent;
          color: #fff;
          border-radius: 13px 13px 0 0;
          font-size: 1.09rem;
          font-weight: 600;
          cursor: pointer;
          transition: background 0.18s, color 0.18s, box-shadow 0.13s;
          margin-right: 4px;
          position: relative;
          border-bottom: 2.7px solid transparent;
          outline: none;
        }
        .tab-modern:focus {
          box-shadow: 0 2px 8px #4f46e527;
        }
        .tab-modern.tab-active {
          background: #fff;
          color: #4f46e5;
          border-bottom: 2.7px solid #22d3ee;
          z-index: 1;
          box-shadow: 0 2px 12px #22d3ee14;
        }
        /* --- Modern Transfer Content Styles --- */
        .transfer-bg {
          background: linear-gradient(120deg, #f8fafc 0%, #e0e7ff 100%);
          min-height: 100vh;
          padding-bottom: 54px;
        }
        .main-modern {
          max-width: 940px;
          margin: 44px auto 0 auto;
          padding: 0 22px;
        }
        .assets-flex {
          display: flex;
          flex-wrap: wrap;
          gap: 26px;
          margin-bottom: 36px;
        }
        .asset-card-modern {
          min-width: 240px;
          max-width: 270px;
          flex: 1 1 245px;
          background: #fff;
          border-radius: 16px;
          box-shadow: 0 4px 32px #4f46e50a;
          padding: 23px 22px 17px 22px;
          display: flex;
          flex-direction: column;
          align-items: flex-start;
          animation: fadeInUp .42s cubic-bezier(.21,1.04,.38,1);
          transition: transform 0.15s;
        }
        .asset-card-modern.selected {
          border: 2.5px solid #0052cc;
          box-shadow: 0 4px 24px #0052cc14;
          background: #f0f7ff;
        }
        .asset-type-modern {
          font-size: 2.3rem;
          margin-bottom: 7px;
          opacity: 0.9;
        }
        .asset-title-modern {
          font-size: 1.15rem;
          font-weight: 700;
          margin-bottom: 7px;
          color: #3730a3;
        }
        .asset-detail-modern {
          color: #69718a;
          font-size: 1.01rem;
          margin-bottom: 3px;
        }
        .asset-id-modern {
          font-size: 0.95rem;
          color: #b0b2c3;
          margin-top: 7px;
        }
        /* Form and buttons */
        .input-modern {
          padding: 8px 12px;
          border-radius: 9px;
          border: 1px solid #d1d5db;
          font-size: 1.09rem;
          margin-bottom: 7px;
        }
        .button-modern {
          background: linear-gradient(90deg,#4f46e5,#06b6d4);
          color: #fff;
          border: none;
          font-weight: 700;
          padding: 12px 0;
          border-radius: 9px;
          font-size: 1.14rem;
          margin-top: 9px;
          box-shadow: 0 2px 9px #4f46e525;
          cursor: pointer;
          transition: background .18s,box-shadow .18s;
        }
        .button-modern:hover {
          background: linear-gradient(90deg,#4f46e5,#22d3ee);
          box-shadow: 0 4px 20px #22d3ee22;
        }
        .pending-offer-card {
          min-width: 300px;
          background: #f7fcf8;
          border-radius: 14px;
          padding: 18px 18px 15px 18px;
          margin-right: 14px;
          margin-bottom: 12px;
          box-shadow: 0 2px 14px #22d3ee14;
        }
        .pending-title {
          font-weight: 700;
          font-size: 1.13rem;
          color: #262a43;
          margin-bottom: 8px;
        }
        .pending-detail {
          font-size: 1.08rem;
          margin-bottom: 6px;
          color: #0377bd;
        }
        .pending-btn {
          background: linear-gradient(90deg,#22d3ee,#4f46e5);
          color: #fff;
          border: none;
          font-weight: 700;
          padding: 8px 0;
          border-radius: 8px;
          font-size: 1.08rem;
          width: 100%;
          margin-top: 11px;
          box-shadow: 0 2px 7px #22d3ee15;
          cursor: pointer;
        }
        .pending-btn:hover {
          background: linear-gradient(90deg,#4f46e5,#06b6d4);
        }
        .transfer-message {
          margin-top: 30px;
          font-weight: 500;
          color: #3a3;
        }
        .transfer-message.error {
          color: #e44;
        }
      `}</style>
      <div className="navbar-modern">
        <div className="nav-top">
          <span>Virtual Wallet</span>
          {address &&
          typeof address === "string" &&
          address.startsWith("0x") &&
          address.length === 42 ? (
            <span className="balance-modern">
              Balance: {parseFloat(balance).toFixed(4)} ETH
            </span>
          ) : null}
        </div>
        <div className="tab-bar">
          <Tab label="Inventory" to="/inventory" />
          <Tab label="Transfer" to="/transfer" />
        </div>
      </div>
      <div className="main-modern">
        <h1 style={{marginBottom: 12, marginTop: 30, fontWeight:700, color:"#262a43"}}>Send Trade Offer</h1>
        <div style={{ marginBottom: 18, color: "#666", fontSize: 15 }}>
          Select an asset, enter recipient wallet, and set price (ETH) to create an offer.<br />
          Recipient must accept and pay for trade to complete!
        </div>
        <div className="assets-flex">
          {nfts.map((nft) => (
            <div
              key={nft.tokenId}
              className={
                "asset-card-modern" + (selected === nft.tokenId ? " selected" : "")
              }
              onClick={() => setSelected(nft.tokenId)}
            >
              <div className="asset-type-modern">
                {nft.itemType === "car" ? "🚗" : "🏠"}
              </div>
              <div className="asset-title-modern">{nft.name}</div>
              <div className="asset-detail-modern">
                {nft.itemType === "car"
                  ? `VIN: ${nft.vin}`
                  : `Address: ${nft.addr}`}
              </div>
              <div className="asset-id-modern">
                NFT ID: {nft.tokenId}
              </div>
            </div>
          ))}
        </div>
        <form
          onSubmit={handleCreateOffer}
          style={{
            margin: "0 auto",
            maxWidth: 340,
            display: "flex",
            flexDirection: "column",
            gap: 9,
            background: "#fff",
            padding: "23px 24px 18px 24px",
            borderRadius: "14px",
            boxShadow: "0 3px 18px #4f46e512",
          }}
        >
          <label htmlFor="to" style={{ fontWeight: 600, marginBottom: 2 }}>
            Recipient Wallet Address
          </label>
          <input
            id="to"
            className="input-modern"
            type="text"
            placeholder="0x..."
            value={to}
            onChange={(e) => setTo(e.target.value)}
          />
          <label htmlFor="amount" style={{ fontWeight: 600, marginBottom: 2 }}>
            Amount (ETH)
          </label>
          <input
            id="amount"
            className="input-modern"
            type="number"
            step="0.0001"
            min="0"
            placeholder="Amount in ETH"
            value={amount}
            onChange={(e) => setAmount(e.target.value)}
          />
          <button className="button-modern" type="submit" disabled={loading}>
            {loading ? "Processing..." : "Create Offer"}
          </button>
        </form>
        <div className="section-title" style={{marginTop:38}}>Pending Offers To You</div>
        <div style={{ color: "#777", fontSize: 15, marginBottom: 10 }}>
          If someone offered you an asset for ETH, you'll see it here.
        </div>
        <div className="assets-flex">
          {offers.length === 0 && <span>No pending offers.</span>}
          {offers.map((offer) => (
            <div
              key={offer.offerId}
              className="pending-offer-card"
            >
              <div className="pending-title">Offer for NFT #{offer.tokenId}</div>
              <div className="pending-detail">
                <span style={{ color: "#0052cc", fontWeight: 600 }}>Price:</span> {offer.price} ETH
              </div>
              <div style={{ fontSize: 13 }}>
                From: <span style={{ color: "#555" }}>{offer.seller.slice(0, 8)}...{offer.seller.slice(-4)}</span>
              </div>
              <button
                className="pending-btn"
                onClick={() => handleAcceptOffer(offer)}
                disabled={loading}
              >
                Accept & Pay
              </button>
            </div>
          ))}
        </div>
        {loading && (
          <div className="transfer-spinner">
            <div className="spinner"></div>
          </div>
        )}
        {message && (
          <div
            className={"transfer-message" + (message.startsWith("Error") ? " error" : "")}
          >
            {message}
          </div>
        )}
      </div>
    </div>
  );
}
