import React, { useEffect, useState } from "react";
import {
  getProvider,
  getMyNFTs,
  createOffer,
  getPendingOffers,
  acceptOffer,
  getEthBalance,
} from "../contractUtils";
import Sidebar from "./Sidebar";
import { useNavigate } from "react-router-dom";

export default function Transfer({ onLogout, user }) {
  const [address, setAddress] = useState(null);
  const [nfts, setNfts] = useState([]);
  const [selected, setSelected] = useState(null);
  const [to, setTo] = useState("");
  const [amount, setAmount] = useState(""); // ETH
  const [message, setMessage] = useState("");
  const [offers, setOffers] = useState([]);
  const [balance, setBalance] = useState("0");
  const navigate = useNavigate();

  useEffect(() => {
    async function fetchData() {
      const provider = await getProvider();
      const signer = provider.getSigner();
      const userAddress = await signer.getAddress();
      setAddress(userAddress);
      setNfts(await getMyNFTs(userAddress));
      setOffers(await getPendingOffers(userAddress));
      if (
        userAddress &&
        typeof userAddress === "string" &&
        userAddress.startsWith("0x") &&
        userAddress.length === 42
      ) {
        setBalance(await getEthBalance(userAddress));
      }
    }
    fetchData();

    const interval = setInterval(async () => {
      if (
        address &&
        typeof address === "string" &&
        address.startsWith("0x") &&
        address.length === 42
      ) {
        setBalance(await getEthBalance(address));
      }
    }, 10000);
    return () => clearInterval(interval);
  }, [address]);

  async function handleCreateOffer(e) {
    e.preventDefault();
    if (!selected || !to || !amount) {
      setMessage("Select an asset, recipient, and amount.");
      return;
    }
    try {
      await createOffer(selected, to, amount);
      setMessage("Offer created! Buyer will see it in their pending offers.");
      setTo("");
      setAmount("");
      setSelected(null);
    } catch (err) {
      setMessage("Error: " + err.message);
    }
  }

  async function handleAcceptOffer(offer) {
    try {
      await acceptOffer(offer.offerId, offer.price);
      setMessage(
        `Offer accepted! You paid ${offer.price} ETH and received the item.`
      );
      setOffers(await getPendingOffers(address));
      setNfts(await getMyNFTs(address));
      if (
        address &&
        typeof address === "string" &&
        address.startsWith("0x") &&
        address.length === 42
      ) {
        setBalance(await getEthBalance(address));
      }
    } catch (err) {
      setMessage("Error accepting offer: " + err.message);
    }
  }

  return (
    <div>
      <Sidebar onLogout={onLogout} />
      <div className="main-content" style={{ marginLeft: 260 }}>
        <div className="header-bar">
          <h1 className="page-title">Send Trade Offer</h1>
          {address &&
          typeof address === "string" &&
          address.startsWith("0x") &&
          address.length === 42 ? (
            <span className="eth-balance">
              Balance: <span style={{ color: "#2a9d44" }}>{parseFloat(balance).toFixed(4)} ETH</span>
            </span>
          ) : null}
        </div>
        <div className="description">
          Select an asset, enter recipient wallet, and set price (ETH) to create an offer.<br />
          Recipient must accept and pay for trade to complete!
        </div>
        <div className="assets-list">
          {nfts.map((nft) => (
            <div
              key={nft.tokenId}
              className={"asset-card" + (selected === nft.tokenId ? " selected" : "")}
              onClick={() => setSelected(nft.tokenId)}
            >
              <div className="asset-type">
                {nft.itemType === "car" ? "🚗" : "🏠"}
              </div>
              <div className="asset-title">{nft.name}</div>
              <div className="asset-detail">
                {nft.itemType === "car"
                  ? `VIN: ${nft.vin}`
                  : `Address: ${nft.addr}`}
              </div>
              <div className="asset-detail">
                Worth: ${nft.value}
              </div>
              <div style={{ fontSize: 12, color: "#888" }}>
                NFT ID: {nft.tokenId}
              </div>
            </div>
          ))}
        </div>
        <form className="offer-form" onSubmit={handleCreateOffer}>
          <label htmlFor="to">Recipient Wallet Address</label>
          <input
            id="to"
            type="text"
            placeholder="0x..."
            value={to}
            onChange={(e) => setTo(e.target.value)}
            style={{ width: "100%" }}
          />
          <label htmlFor="amount">Amount (ETH)</label>
          <input
            id="amount"
            type="number"
            step="0.0001"
            min="0"
            placeholder="Amount in ETH"
            value={amount}
            onChange={(e) => setAmount(e.target.value)}
            style={{ width: "100%" }}
          />
          <button className="button-main" type="submit">
            Create Offer
          </button>
        </form>

        {/* Pending Offers */}
        <div className="pending-offers-card">
          <div className="pending-title">Pending Offers To You</div>
          <div className="pending-desc">If someone offered you an asset for ETH, you'll see it here.</div>
          <div>
            {offers.length === 0 ? (
              <div className="no-offers">No pending offers.</div>
            ) : (
              offers.map((offer) => (
                <div
                  key={offer.offerId}
                  className="asset-card"
                  style={{ minWidth: 300, background: "#f7fcf8" }}
                >
                  <div className="asset-title" style={{ marginBottom: 10 }}>
                    Offer for NFT #{offer.tokenId}
                  </div>
                  <div style={{ marginBottom: 8 }}>
                    <span style={{ color: "#0052cc", fontWeight: 600 }}>Price:</span> {offer.price} ETH
                  </div>
                  <div style={{ fontSize: 13 }}>
                    From: <span style={{ color: "#555" }}>{offer.seller.slice(0, 8)}...{offer.seller.slice(-4)}</span>
                  </div>
                  <button
                    className="button-main"
                    style={{ marginTop: 16 }}
                    onClick={() => handleAcceptOffer(offer)}
                  >
                    Accept & Pay
                  </button>
                </div>
              ))
            )}
          </div>
        </div>

        {message && (
          <div
            style={{
              marginTop: 30,
              marginBottom: 10,
              fontWeight: 500,
              color:
                message.includes("success") || message.includes("created")
                  ? "#3a3"
                  : "#e44",
            }}
          >
            {message}
          </div>
        )}
      </div>
      <style>{`
        .main-content {
          padding: 34px 18px 38px 18px;
          min-height: 100vh;
          background: linear-gradient(120deg,#f8fafc 80%,#e0e7ff 100%);
        }
        .header-bar {
          display: flex;
          align-items: center;
          justify-content: space-between;
          margin-bottom: 10px;
        }
        .page-title {
          font-size: 2.0rem;
          margin: 0;
          font-weight: 700;
          color: #3730a3;
        }
        .eth-balance {
          font-size: 1.12rem;
          font-weight: 500;
          margin-right: 16px;
        }
        .description {
          margin-bottom: 24px;
          color: #666;
          font-size: 15px;
        }
        .assets-list {
          display: flex;
          gap: 32px;
          flex-wrap: wrap;
          margin-bottom: 38px;
        }
        .asset-card {
          background: #fff;
          border-radius: 13px;
          box-shadow: 0 2px 11px #6366f12b;
          padding: 16px 19px;
          min-width: 180px;
          cursor: pointer;
          border: 2px solid transparent;
          transition: border .18s, box-shadow .18s, background .18s;
        }
        .asset-card.selected,
        .asset-card:active {
          border: 2.2px solid #4f46e5;
          box-shadow: 0 4px 22px #6366f113;
          background: #f0f7ff;
        }
        .asset-type {
          font-size: 1.8rem;
        }
        .asset-title {
          font-weight: 700;
          font-size: 1.08rem;
          margin: 4px 0 4px 0;
        }
        .asset-detail {
          color: #555;
          font-size: 0.98rem;
        }
        .offer-form {
          margin: 0 auto 0 0;
          max-width: 340px;
          display: flex;
          flex-direction: column;
          gap: 15px;
          padding: 18px 0;
        }
        .button-main {
