import React, { useEffect, useState } from "react";
import {
  getProvider,
  getMyNFTs,
  createOffer,
  getPendingOffers,
  acceptOffer,
  getEthBalance,
} from "../contractUtils";
import { useNavigate } from "react-router-dom";

export default function Transfer() {
  const [address, setAddress] = useState(null);
  const [nfts, setNfts] = useState([]);
  const [selected, setSelected] = useState(null);
  const [to, setTo] = useState("");
  const [amount, setAmount] = useState("");
  const [message, setMessage] = useState("");
  const [offers, setOffers] = useState([]);
  const [balance, setBalance] = useState("0");
  const [loading, setLoading] = useState(false);
  const navigate = useNavigate();

  useEffect(() => {
    async function fetchData() {
      const provider = await getProvider();
      const signer = provider.getSigner();
      const userAddress = await signer.getAddress();
      setAddress(userAddress);
      setNfts(await getMyNFTs(userAddress));
      setOffers(await getPendingOffers(userAddress));
      if (
        userAddress &&
        typeof userAddress === "string" &&
        userAddress.startsWith("0x") &&
        userAddress.length === 42
      ) {
        setBalance(await getEthBalance(userAddress));
      }
    }
    fetchData();

    const interval = setInterval(async () => {
      if (
        address &&
        typeof address === "string" &&
        address.startsWith("0x") &&
        address.length === 42
      ) {
        setBalance(await getEthBalance(address));
      }
    }, 10000);
    return () => clearInterval(interval);
  }, [address]);

  async function handleCreateOffer(e) {
    e.preventDefault();
    setMessage("");
    setLoading(true);
    if (!selected || !to || !amount) {
      setMessage("Select an asset, recipient, and amount.");
      setLoading(false);
      return;
    }
    try {
      await createOffer(selected, to, amount);
      setMessage("Offer created! Buyer will see it in their pending offers.");
      setTo("");
      setAmount("");
      setSelected(null);
      setLoading(false);
    } catch (err) {
      setMessage("Error: " + err.message);
      setLoading(false);
    }
  }

  async function handleAcceptOffer(offer) {
    setLoading(true);
    try {
      await acceptOffer(offer.offerId, offer.price);
      setMessage(
        `Offer accepted! You paid ${offer.price} ETH and received the item.`
      );
      setOffers(await getPendingOffers(address));
      setNfts(await getMyNFTs(address));
      if (
        address &&
        typeof address === "string" &&
        address.startsWith("0x") &&
        address.length === 42
      ) {
        setBalance(await getEthBalance(address));
      }
      setLoading(false);
    } catch (err) {
      setMessage("Error accepting offer: " + err.message);
      setLoading(false);
    }
  }

  return (
    <div className="transfer-bg">
      <style>{`
        .navbar-modern {
          width: 100%;
          background: linear-gradient(90deg, #4f46e5 0%, #5eead4 100%);
          color: #fff;
          padding: 16px 32px;
          font-size: 1.3rem;
          font-weight: 700;
          border-radius: 0 0 18px 18px;
          display: flex;
          justify-content: space-between;
          align-items: center;
          box-shadow: 0 3px 20px #0002;
        }
        .balance-modern {
          font-size: 1.09rem;
          font-weight: 600;
          background: #fff;
          color: #22c55e;
          border-radius: 8px;
          padding: 3px 16px;
          margin-left: 20px;
        }
        .transfer-bg {
          background: linear-gradient(120deg, #f8fafc 0%, #e0e7ff 100%);
          min-height: 100vh;
          padding-bottom: 54px;
        }
        .main-modern {
          max-width: 940px;
          margin: 44px auto 0 auto;
          padding: 0 22px;
        }
        .assets-flex {
          display: flex;
          flex-wrap: wrap;
          gap: 26px;
          margin-bottom: 36px;
        }
        .asset-card-modern {
          min-width: 220px;
          max-width: 260px;
          flex: 1 1 230px;
          background: #fff;
          border-radius: 16px;
          box-shadow: 0 4px 32px #4f46e50a;
          padding: 18px 20px 14px 20px;
          display: flex;
          flex-direction: column;
          align-items: flex-start;
          cursor: pointer;
          border: 2.1px solid transparent;
          animation: fadeInUp .44s cubic-bezier(.21,1.04,.38,1);
          transition: border 0.17s, box-shadow 0.15s, transform 0.14s;
        }
        .asset-card-modern.selected, .asset-card-modern:hover {
          border: 2.1px solid #4f46e5;
          box-shadow: 0 8px 44px #4f46e524;
          transform: scale(1.025);
          background: #f7faff;
        }
        .asset-type-modern {
          font-size: 2.1rem;
          margin-bottom: 5px;
          opacity: 0.92;
        }
        .asset-title-modern {
          font-size: 1.09rem;
          font-weight: 700;
          margin-bottom: 7px;
          color: #3730a3;
        }
        .asset-detail-modern {
          color: #69718a;
          font-size: 1.01rem;
          margin-bottom: 2px;
        }
        .asset-id-modern {
          font-size: 0.94rem;
          color: #b0b2c3;
          margin-top: 6px;
        }
        .input-modern {
          width: 100%;
          border: 1.5px solid #e2e8f0;
          border-radius: 7px;
          padding: 11px 11px;
          font-size: 1rem;
          margin-bottom: 14px;
          background: none;
          transition: border 0.18s;
        }
        .input-modern:focus {
          border-color: #5c7cfa;
        }
        .button-modern {
          background: linear-gradient(90deg, #4f46e5 0%, #5eead4 100%);
          color: #fff;
          font-weight: 700;
          font-size: 1.09rem;
          padding: 11px 36px;
          border-radius: 11px;
          border: none;
          cursor: pointer;
          margin-top: 18px;
          transition: background 0.16s, transform 0.1s;
          box-shadow: 0 4px 18px #4f46e51a;
        }
        .button-modern:hover {
          background: linear-gradient(90deg, #4338ca 0%, #0ea5e9 100%);
          transform: translateY(-2px) scale(1.03);
        }
        .section-title {
          font-size: 1.24rem;
          font-weight: 600;
          margin: 36px 0 16px 0;
          color: #3730a3;
        }
        .pending-offer-card {
          background: #fff;
          border-radius: 13px;
          box-shadow: 0 3px 16px #4f46e512;
          padding: 17px 23px;
          min-width: 290px;
          margin-bottom: 13px;
          animation: fadeInUp .33s cubic-bezier(.21,1.04,.38,1);
        }
        .pending-title {
          font-size: 1.06rem;
          font-weight: 700;
          margin-bottom: 7px;
          color: #3730a3;
        }
        .pending-detail {
          color: #3b3e5a;
          font-size: 1.01rem;
        }
        .pending-btn {
          margin-top: 13px;
          background: linear-gradient(92deg, #16c2b8 0%, #4f46e5 100%);
          color: #fff;
          font-weight: 600;
          border-radius: 7px;
          border: none;
          padding: 9px 18px;
          font-size: 1.01rem;
          cursor: pointer;
          transition: background 0.14s;
        }
        .pending-btn:hover {
          background: linear-gradient(92deg, #0ea5e9 0%, #4338ca 100%);
        }
        .transfer-message {
          margin: 24px 0 0 0;
          font-weight: 500;
          text-align: center;
          color: #4f46e5;
        }
        .transfer-message.error {
          color: #e3342f;
        }
        .transfer-spinner {
          display: flex;
          align-items: center;
          justify-content: center;
          margin: 17px 0 10px 0;
        }
        .spinner {
          border: 3px solid #e0e7ff;
          border-top: 3px solid #4f46e5;
          border-radius: 50%;
          width: 32px;
          height: 32px;
          animation: spin 0.7s linear infinite;
        }
        @keyframes spin {
          0% { transform: rotate(0deg);}
          100% { transform: rotate(360deg);}
        }
      `}</style>
      <div className="navbar-modern">
        Virtual Wallet
        {address &&
        typeof address === "string" &&
        address.startsWith("0x") &&
        address.length === 42 ? (
          <span className="balance-modern">
            Balance: {parseFloat(balance).toFixed(4)} ETH
          </span>
        ) : null}
      </div>
      <div className="main-modern">
        <h1 style={{marginBottom: 12, marginTop: 30, fontWeight:700, color:"#262a43"}}>Send Trade Offer</h1>
        <div style={{ marginBottom: 18, color: "#666", fontSize: 15 }}>
          Select an asset, enter recipient wallet, and set price (ETH) to create an offer.<br />
          Recipient must accept and pay for trade to complete!
        </div>
        <div className="assets-flex">
          {nfts.map((nft) => (
            <div
              key={nft.tokenId}
              className={
                "asset-card-modern" + (selected === nft.tokenId ? " selected" : "")
              }
              onClick={() => setSelected(nft.tokenId)}
            >
              <div className="asset-type-modern">
                {nft.itemType === "car" ? "🚗" : "🏠"}
              </div>
              <div className="asset-title-modern">{nft.name}</div>
              <div className="asset-detail-modern">
                {nft.itemType === "car"
                  ? `VIN: ${nft.vin}`
                  : `Address: ${nft.addr}`}
              </div>
              <div className="asset-id-modern">
                NFT ID: {nft.tokenId}
              </div>
            </div>
          ))}
        </div>
        <form
          onSubmit={handleCreateOffer}
          style={{
            margin: "0 auto",
            maxWidth: 340,
            display: "flex",
            flexDirection: "column",
            gap: 9,
            background: "#fff",
            padding: "23px 24px 18px 24px",
            borderRadius: "14px",
            boxShadow: "0 3px 18px #4f46e512",
          }}
        >
          <label htmlFor="to" style={{ fontWeight: 600, marginBottom: 2 }}>
            Recipient Wallet Address
          </label>
          <input
            id="to"
            className="input-modern"
            type="text"
            placeholder="0x..."
            value={to}
            onChange={(e) => setTo(e.target.value)}
          />
          <label htmlFor="amount" style={{ fontWeight: 600, marginBottom: 2 }}>
            Amount (ETH)
          </label>
          <input
            id="amount"
            className="input-modern"
            type="number"
            step="0.0001"
            min="0"
            placeholder="Amount in ETH"
            value={amount}
            onChange={(e) => setAmount(e.target.value)}
          />
          <button className="button-modern" type="submit" disabled={loading}>
            {loading ? "Processing..." : "Create Offer"}
          </button>
        </form>
        <div className="section-title" style={{marginTop:38}}>Pending Offers To You</div>
        <div style={{ color: "#777", fontSize: 15, marginBottom: 10 }}>
          If someone offered you an asset for ETH, you'll see it here.
        </div>
        <div className="assets-flex">
          {offers.length === 0 && <span>No pending offers.</span>}
          {offers.map((offer) => (
            <div
              key={offer.offerId}
              className="pending-offer-card"
            >
              <div className="pending-title">Offer for NFT #{offer.tokenId}</div>
              <div className="pending-detail">
                <span style={{ color: "#0052cc", fontWeight: 600 }}>Price:</span> {offer.price} ETH
              </div>
              <div style={{ fontSize: 13 }}>
                From: <span style={{ color: "#555" }}>{offer.seller.slice(0, 8)}...{offer.seller.slice(-4)}</span>
              </div>
              <button
                className="pending-btn"
                onClick={() => handleAcceptOffer(offer)}
                disabled={loading}
              >
                Accept & Pay
              </button>
            </div>
          ))}
        </div>
        {loading && (
          <div className="transfer-spinner">
            <div className="spinner"></div>
          </div>
        )}
        {message && (
          <div
            className={"transfer-message" + (message.startsWith("Error") ? " error" : "")}
          >
            {message}
          </div>
        )}
        <button className="button-modern" style={{marginTop:32}} onClick={() => navigate("/inventory")}>
          Back to Inventory
        </button>
      </div>
    </div>
  );
}
