import React, { useEffect, useState } from "react";
import { getProvider, getMyNFTs, transferNFT } from "../contractUtils";
import { useNavigate } from "react-router-dom";

export default function Transfer() {
  const [address, setAddress] = useState("");
  const [nfts, setNfts] = useState([]);
  const [selected, setSelected] = useState(null);
  const [to, setTo] = useState("");
  const [message, setMessage] = useState("");
  const navigate = useNavigate();

  useEffect(() => {
    async function fetchData() {
      const provider = await getProvider();
      const signer = provider.getSigner();
      const userAddress = await signer.getAddress();
      setAddress(userAddress);
      setNfts(await getMyNFTs(userAddress));
    }
    fetchData();
  }, []);

  async function handleTransfer(e) {
    e.preventDefault();
    if (!selected || !to) {
      setMessage("Please select an item and enter a recipient address.");
      return;
    }
    try {
      await transferNFT(selected, to);
      setMessage("Transferred successfully!");
      setNfts(nfts.filter(nft => nft.tokenId !== selected));
    } catch (err) {
      setMessage("Error transferring: " + err.message);
    }
  }

  return (
    <div>
      <div style={{ padding: 20, fontWeight: "bold", fontSize: 28 }}>Transfers</div>
      <div style={{ textAlign: "center", marginTop: 24 }}>
        <h1>Items You Own</h1>
        <div style={{ display: "flex", justifyContent: "center", gap: 60, marginBottom: 32 }}>
          {nfts.map(nft => (
            <div
              key={nft.tokenId}
              onClick={() => setSelected(nft.tokenId)}
              style={{
                cursor: "pointer",
                border: selected === nft.tokenId ? "2px solid #000" : "1px solid #ccc",
                borderRadius: 16,
                padding: 20,
                background: selected === nft.tokenId ? "#f7f7f7" : "#fff",
                minWidth: 120,
              }}
            >
              <div style={{ fontSize: 42 }}>
                {nft.itemType === "car" ? "🚗" : "🏠"}
              </div>
              <div>{nft.name}</div>
            </div>
          ))}
        </div>
        <div style={{ margin: "40px auto", width: 400, textAlign: "center" }}>
          <h2>Transfer Asset</h2>
          <form onSubmit={handleTransfer}>
            <div style={{ marginBottom: 10 }}>
              <input
                type="text"
                placeholder="Recipient Address"
                value={to}
                onChange={e => setTo(e.target.value)}
                style={{ width: "100%", padding: 8 }}
              />
              <div style={{ fontSize: 12, color: "#888" }}>Enter wallet address</div>
            </div>
            <button type="submit" style={{ width: "100%", background: "#000", color: "#fff", padding: 10, fontWeight: "bold", fontSize: 16, borderRadius: 4 }}>
              Transfer
            </button>
          </form>
          {message && <div style={{ marginTop: 10 }}>{message}</div>}
        </div>
      </div>
    </div>
  );
}
