import React, { useState, useEffect } from "react";
import { BrowserRouter as Router, Routes, Route, Navigate } from "react-router-dom";
import Navbar from "./components/Navbar";
import Inventory from "./components/Inventory";
import Transfer from "./components/Transfer";
import About from "./components/About";
import Login from "./components/Login";
import { getEthBalance } from "./contractUtils"; // <-- Import balance fetcher!

function App() {
  const [user, setUser] = useState(null);
  const [balance, setBalance] = useState("0"); // Casino balance state!
  const [search, setSearch] = useState("");

  // Fetch the ETH balance when the user logs in or changes address
  useEffect(() => {
    let interval;
    async function fetchBalance() {
      if (user && user.address) {
        const bal = await getEthBalance(user.address);
        setBalance(bal);
      }
    }
    fetchBalance();
    // Optionally, refresh every 10s like a casino site
    if (user && user.address) {
      interval = setInterval(fetchBalance, 10000);
    }
    return () => interval && clearInterval(interval);
  }, [user]);

  // Handle search bar submit
  function handleSearch(e) {
    e.preventDefault();
    setSearch(search.trim());
  }

  // Handle logout
  const handleLogout = () => {
    setUser(null);
    setBalance("0");
    setSearch("");
  };

  return (
    <Router>
      {user && (
        <Navbar
          onLogout={handleLogout}
          search={search}
          setSearch={setSearch}
          handleSearch={handleSearch}
          balance={balance} // Casino-style: pass balance everywhere!
        />
      )}
      <Routes>
        {!user ? (
          <Route path="*" element={<Login onLogin={setUser} />} />
        ) : (
          <>
            <Route
              path="/inventory"
              element={
                <Inventory
                  user={user}
                  search={search}
                  setSearch={setSearch}
                  balance={balance}
                />
              }
            />
            <Route
              path="/transfer"
              element={<Transfer user={user} balance={balance} />}
            />
            <Route
              path="/about"
              element={<About balance={balance} />}
            />
            <Route path="*" element={<Navigate to="/inventory" />} />
          </>
        )}
      </Routes>
    </Router>
  );
}

export default App;
