// src/components/Navbar.js
import React, { useState } from "react";
import { useLocation, useNavigate } from "react-router-dom";

export default function Navbar({ onSearch, onLogout, walletAddress }) {
  const location = useLocation();
  const navigate = useNavigate();
  const [search, setSearch] = useState("");
  const [showAccount, setShowAccount] = useState(false);

  function handleSearch(e) {
    e.preventDefault();
    onSearch && onSearch(search);
  }

  return (
    <div className="navbar-modern">
      <div className="nav-top">
        {/* Tabs */}
        <div className="tab-bar">
          <div
            className={`tab-modern${location.pathname === "/inventory" ? " tab-active" : ""}`}
            onClick={() => navigate("/inventory")}
            tabIndex={0}
          >
            INVENTORY
          </div>
          <div
            className={`tab-modern${location.pathname === "/transfer" ? " tab-active" : ""}`}
            onClick={() => navigate("/transfer")}
            tabIndex={0}
          >
            TRANSFER
          </div>
          <div
            className={`tab-modern${location.pathname === "/about" ? " tab-active" : ""}`}
            onClick={() => navigate("/about")}
            tabIndex={0}
          >
            ABOUT
          </div>
        </div>
        {/* Logo/Brand */}
        <span className="brand-1312">1312</span>
        {/* Right Side: Search, Account, Logout */}
        <div style={{ display: "flex", alignItems: "center" }}>
          <form className="search-modern" onSubmit={handleSearch}>
            <input
              className="search-input-modern"
              placeholder="Search..."
              value={search}
              onChange={(e) => setSearch(e.target.value)}
            />
            <button className="search-btn-modern" type="submit">SEARCH</button>
          </form>
          <button className="account-modern" onClick={() => setShowAccount(true)}>
            ACCOUNT
          </button>
          <button className="logout-modern" onClick={onLogout}>
            LOG OUT
          </button>
        </div>
      </div>

      {/* Account Modal */}
      {showAccount && (
        <div
          style={{
            position: "fixed",
            top: 80,
            right: 30,
            zIndex: 10,
            background: "#fff",
            padding: 24,
            borderRadius: 14,
            boxShadow: "0 4px 24px #0002",
            minWidth: 310,
            maxWidth: 340,
            textAlign: "center",
          }}
        >
          <div style={{ fontWeight: 700, fontSize: 20, marginBottom: 14 }}>
            Your Account
          </div>
          <div style={{ marginBottom: 18 }}>
            <span style={{ fontWeight: 600 }}>Wallet:</span>
            <div style={{
              wordBreak: "break-all",
              marginTop: 7,
              color: "#5047d8",
              fontSize: 15,
              background: "#f1f5f9",
              padding: 8,
              borderRadius: 8,
              userSelect: "all"
            }}>
              {walletAddress || "No wallet connected"}
            </div>
          </div>
          <button
            className="button-modern"
            style={{ marginTop: 8, background: "#f1f1f1", color: "#333" }}
            onClick={() => setShowAccount(false)}
          >
            Close
          </button>
        </div>
      )}
      {/* (Optional) Overlay for modal */}
      {showAccount && (
        <div
          style={{
            position: "fixed",
            inset: 0,
            zIndex: 5,
            background: "rgba(24,30,50,0.06)"
          }}
          onClick={() => setShowAccount(false)}
        />
      )}
    </div>
  );
}
