import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import { getProvider } from "../contractUtils";

export default function Login() {
  const [cnp, setCnp] = useState("");
  const [password, setPassword] = useState("");
  const [error, setError] = useState("");
  const navigate = useNavigate();

  const handleMetaMask = async (e) => {
    e.preventDefault();
    try {
      await getProvider();
      navigate("/inventory");
    } catch {
      setError("Please install MetaMask.");
    }
  };

  return (
    <div style={{ minHeight: "100vh", display: "flex", flexDirection: "column" }}>
      <div style={{ padding: 20, fontWeight: "bold", fontSize: 28 }}>Virtual Wallet</div>
      <div style={{ flex: 1, display: "flex", flexDirection: "column", alignItems: "center", justifyContent: "center" }}>
        <form onSubmit={handleMetaMask} style={{ width: 400, textAlign: "center" }}>
          <h1>Login</h1>
          <p>Please provide your credentials</p>
          <div style={{ textAlign: "left", marginBottom: 20 }}>
            <label>CNP</label>
            <input
              type="text"
              placeholder="Enter your CNP"
              value={cnp}
              onChange={e => setCnp(e.target.value)}
              style={{ width: "100%", padding: 8, marginTop: 6 }}
            />
            <div style={{ fontSize: 12, color: "#888" }}>Example: 1234567890123</div>
          </div>
          <div style={{ textAlign: "left", marginBottom: 20 }}>
            <label>Password</label>
            <input
              type="password"
              placeholder="Enter your password"
              value={password}
              onChange={e => setPassword(e.target.value)}
              style={{ width: "100%", padding: 8, marginTop: 6 }}
            />
            <div style={{ fontSize: 12, color: "#888" }}>Minimum 8 characters</div>
          </div>
          <button
            type="submit"
            style={{ width: "100%", background: "#000", color: "#fff", padding: 10, fontWeight: "bold", fontSize: 16, borderRadius: 4 }}
          >
            Login
          </button>
          {error && <div style={{ color: "red", marginTop: 10 }}>{error}</div>}
        </form>
      </div>
      <div style={{ display: "flex", justifyContent: "center", gap: 40, padding: 40 }}>
        <span>Contact Us</span>
        <span>Help</span>
        <span>Terms of Service</span>
      </div>
    </div>
  );
}
