import React, { useState, useRef, useEffect } from "react";
import { Link, useLocation } from "react-router-dom";

// Minimal blocky avatar from address (works with any short string)
function AddressAvatar({ address, size = 36 }) {
  // simple colors from address hash
  const hash = address ? address.split("").reduce((a, b) => a + b.charCodeAt(0), 0) : 127;
  const color1 = `hsl(${hash % 360},70%,63%)`;
  const color2 = `hsl(${(hash * 5) % 360},60%,82%)`;
  return (
    <span
      style={{
        width: size,
        height: size,
        borderRadius: "16%",
        background: `linear-gradient(135deg, ${color1} 50%, ${color2} 100%)`,
        display: "inline-block",
        position: "relative",
        boxShadow: "0 2px 9px #262cff1a"
      }}
    >
      <span style={{
        position: "absolute", width: "60%", height: "60%",
        background: "#fff6", left: "20%", top: "20%", borderRadius: "13%"
      }} />
      {/* Optional: 1 char of address */}
      <span style={{
        position: "absolute", top: "47%", left: "51%", transform: "translate(-50%,-50%)",
        color: "#23283b", fontWeight: 900, fontSize: size * 0.47, opacity: 0.8
      }}>{address?.[2]?.toUpperCase() ?? "?"}</span>
    </span>
  );
}

export default function Navbar({
  balance,
  onLogout,
  search,
  setSearch,
  handleSearch,
  user,   // <-- Pass user (with address)
}) {
  const location = useLocation();
  const [showProfile, setShowProfile] = useState(false);
  const dropdownRef = useRef();

  // Hide profile dropdown on outside click
  useEffect(() => {
    function handler(e) {
      if (dropdownRef.current && !dropdownRef.current.contains(e.target)) {
        setShowProfile(false);
      }
    }
    if (showProfile) {
      document.addEventListener("mousedown", handler);
    }
    return () => document.removeEventListener("mousedown", handler);
  }, [showProfile]);

  const shortAddr = user?.address
    ? user.address.slice(0, 6) + "..." + user.address.slice(-4)
    : "Not connected";

  // Click to copy address
  function copyAddr() {
    if (user?.address) {
      navigator.clipboard.writeText(user.address);
    }
  }

  return (
    <header className="navbar-bg">
      <nav className="navbar">
        <div className="navbar-logo-section">
          <span className="navbar-logo">
            Rares<span className="dot">.</span>
          </span>
          <div className="navbar-menu">
            <Link to="/inventory" className={location.pathname === "/inventory" ? "navbar-link active" : "navbar-link"}>
              Inventory
            </Link>
            <Link to="/transfer" className={location.pathname === "/transfer" ? "navbar-link active" : "navbar-link"}>
              Transfer
            </Link>
            <Link to="/offers" className={location.pathname === "/offers" ? "navbar-link active" : "navbar-link"}>
              Offers
            </Link>
            <Link to="/about" className={location.pathname === "/about" ? "navbar-link active" : "navbar-link"}>
              About
            </Link>
          </div>
        </div>
        <div className="navbar-actions">
          <form className="navbar-search" onSubmit={handleSearch} autoComplete="off">
            <input
              type="text"
              placeholder="Searchâ€¦"
              value={search}
              onChange={e => setSearch(e.target.value)}
            />
            <button type="submit">
              <span style={{ fontWeight: 600 }}>Search</span>
            </button>
          </form>
          <div className="navbar-balance-chip" title="Wallet balance">
            <span className="coin-emoji" role="img" aria-label="coin">ðŸª™</span>
            <span className="navbar-balance-amount">
              {balance ? parseFloat(balance).toFixed(4) : "0.0000"}
            </span>
            <span className="navbar-balance-currency">ETH</span>
          </div>
          {/* PROFILE AVATAR & DROPDOWN */}
          <div className="navbar-profile" ref={dropdownRef}>
            <button
              className="navbar-avatar-btn"
              title="Profile"
              onClick={() => setShowProfile((v) => !v)}
              aria-label="Open profile menu"
              type="button"
            >
              <AddressAvatar address={user?.address || ""} />
              <span className="profile-caret">{showProfile ? "â–²" : "â–¼"}</span>
            </button>
            {showProfile && (
              <div className="profile-dropdown">
                <div className="profile-section" style={{ borderBottom: "1px solid #eee" }}>
                  <AddressAvatar address={user?.address || ""} size={56} />
                  <div className="profile-addr-group">
                    <span className="profile-label">Wallet</span>
                    <span
                      className="profile-address"
                      title={user?.address}
                      style={{ cursor: user?.address ? "pointer" : "default" }}
                      onClick={copyAddr}
                    >
                      {shortAddr}
                      <span style={{
                        fontSize: 13,
                        marginLeft: 7,
                        opacity: 0.7,
                        color: "#226cf6"
                      }}>copy</span>
                    </span>
                  </div>
                  <div className="profile-balance">
                    <span className="profile-label">Balance:</span>
                    <span style={{ fontWeight: 900, color: "#262cff", marginLeft: 8 }}>
                      {balance ? parseFloat(balance).toFixed(4) : "0.0000"} ETH
                    </span>
                  </div>
                </div>
                <div className="profile-actions">
                  <button className="profile-action-btn" onClick={onLogout}>
                    Logout
                  </button>
                </div>
              </div>
            )}
          </div>
        </div>
      </nav>
      <style>{`
        /* ...existing CSS... */
        .navbar-profile {
          position: relative;
          margin-left: 12px;
        }
        .navbar-avatar-btn {
          background: none;
          border: none;
          padding: 0;
          display: flex;
          align-items: center;
          cursor: pointer;
          transition: filter .15s;
          border-radius: 16px;
          outline: none;
        }
        .navbar-avatar-btn:focus,
        .navbar-avatar-btn:hover {
          filter: brightness(0.94);
        }
        .profile-caret {
          font-size: 1.05em;
          margin-left: 6px;
          color: #868ca2;
          font-weight: 700;
          pointer-events: none;
        }
        .profile-dropdown {
          position: absolute;
          right: 0;
          top: 48px;
          z-index: 99;
          min-width: 220px;
          background: #fff;
          border-radius: 13px;
          box-shadow: 0 8px 30px #262cff13, 0 1.5px 9px #e2e6ff1c;
          padding: 19px 17px 11px 17px;
          display: flex;
          flex-direction: column;
          gap: 12px;
          animation: profileDropdownIn .17s;
        }
        @keyframes profileDropdownIn {
          from { opacity: 0; transform: translateY(-10px);}
          to { opacity: 1; transform: translateY(0);}
        }
        .profile-section {
          display: flex;
          flex-direction: column;
          align-items: center;
          gap: 10px;
          padding-bottom: 9px;
        }
        .profile-addr-group {
          text-align: center;
          width: 100%;
        }
        .profile-label {
          font-size: 0.93em;
          color: #9da7c1;
          font-weight: 700;
        }
        .profile-address {
          font-family: monospace;
          font-size: 1.09em;
          color: #262cff;
          font-weight: 700;
          letter-spacing: 0.01em;
          display: inline-block;
          transition: color .13s;
        }
        .profile-address:hover {
          color: #192a51;
        }
        .profile-balance {
          font-size: 1.08em;
          margin-top: 2px;
        }
        .profile-actions {
          display: flex;
          flex-direction: column;
          gap: 8px;
          margin-top: 8px;
        }
        .profile-action-btn {
          background: linear-gradient(93deg,#f2f5ff 0%,#f5f9ff 100%);
          color: #e11d48;
          font-weight: 900;
          border: none;
          border-radius: 8px;
          padding: 8px 0;
          font-size: 1.06rem;
          cursor: pointer;
          transition: background .15s,color .13s;
        }
        .profile-action-btn:hover {
          background: #fff6f7;
          color: #181a23;
        }
        /* ...rest of your existing CSS below... */
      `}</style>
    </header>
  );
}
