import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import { getProvider, getEthBalance } from "../contractUtils";

export default function Login() {
  const [cnp, setCnp] = useState("");
  const [password, setPassword] = useState("");
  const [error, setError] = useState("");
  const [balance, setBalance] = useState(null);
  const [address, setAddress] = useState("");
  const navigate = useNavigate();

  const handleMetaMask = async (e) => {
    e.preventDefault();
    try {
      const provider = await getProvider();
      const signer = provider.getSigner();
      const userAddress = await signer.getAddress();
      setAddress(userAddress);
      if (userAddress && userAddress.startsWith("0x") && userAddress.length === 42) {
        setBalance(await getEthBalance(userAddress));
      }
      navigate("/inventory");
    } catch {
      setError("Please install MetaMask and connect to the Hardhat network.");
    }
  };

  return (
    <div style={{ minHeight: "100vh", background: "#f8f9fa" }}>
      <div className="navbar">
        Virtual Wallet
        {address && address.startsWith("0x") && address.length === 42 && balance !== null ? (
          <span style={{float:"right",fontWeight:"400",fontSize:"18px"}}>
            Balance: <span style={{color:"#2a9d44"}}>{parseFloat(balance).toFixed(4)} ETH</span>
          </span>
        ) : null}
      </div>
      <div className="main-content" style={{
        maxWidth: 430, marginTop: 80, marginBottom: 80, boxShadow: "0 8px 24px #0001"
      }}>
        <form onSubmit={handleMetaMask} style={{ display: "flex", flexDirection: "column", gap: 17 }}>
          <h2 style={{ margin: 0 }}>Log in</h2>
          <div style={{ color: "#888", fontSize: 15, marginBottom: 18 }}>
            Connect your wallet to continue.
          </div>
          <div>
            <label htmlFor="cnp">CNP</label>
            <input
              id="cnp"
              type="text"
              placeholder="Enter your CNP"
              value={cnp}
              onChange={e => setCnp(e.target.value)}
              style={{ width: "100%" }}
              autoComplete="username"
            />
          </div>
          <div>
            <label htmlFor="pw">Password</label>
            <input
              id="pw"
              type="password"
              placeholder="Enter your password"
              value={password}
              onChange={e => setPassword(e.target.value)}
              style={{ width: "100%" }}
              autoComplete="current-password"
            />
          </div>
          <button className="button-main" type="submit" style={{ marginTop: 10, width: "100%" }}>
            Log in with MetaMask
          </button>
          {error && <div style={{ color: "red", marginTop: 10 }}>{error}</div>}
        </form>
        <div style={{
          display: "flex",
          justifyContent: "center",
          gap: 36,
          marginTop: 35,
          color: "#888",
          fontSize: 14
        }}>
          <span>Contact Us</span>
          <span>Help</span>
          <span>Terms of Service</span>
        </div>
      </div>
    </div>
  );
}
