import React, { useEffect, useState } from "react";
import { getProvider, getMyNFTs, getEthBalance } from "../contractUtils";
import { useNavigate, useLocation } from "react-router-dom";

export default function Inventory() {
  const [address, setAddress] = useState(null);
  const [cars, setCars] = useState([]);
  const [houses, setHouses] = useState([]);
  const [balance, setBalance] = useState("0");
  const navigate = useNavigate();
  const location = useLocation();

  useEffect(() => {
    async function fetchData() {
      const provider = await getProvider();
      const signer = provider.getSigner();
      const userAddress = await signer.getAddress();
      setAddress(userAddress);
      const nfts = await getMyNFTs(userAddress);
      setCars(nfts.filter(nft => nft.itemType === "car"));
      setHouses(nfts.filter(nft => nft.itemType === "house"));
      if (
        userAddress &&
        typeof userAddress === "string" &&
        userAddress.startsWith("0x") &&
        userAddress.length === 42
      ) {
        setBalance(await getEthBalance(userAddress));
      }
    }
    fetchData();

    const interval = setInterval(async () => {
      if (
        address &&
        typeof address === "string" &&
        address.startsWith("0x") &&
        address.length === 42
      ) {
        setBalance(await getEthBalance(address));
      }
    }, 10000);
    return () => clearInterval(interval);
  }, [address]);

  // --- Modern Tab NavBar ---
  function Tab({ label, to }) {
    const active = location.pathname === to;
    return (
      <div
        className={`tab-modern${active ? " tab-active" : ""}`}
        onClick={() => navigate(to)}
        tabIndex={0}
      >
        {label}
      </div>
    );
  }

  return (
    <div className="inventory-bg">
      <style>{`
        .navbar-modern {
          width: 100%;
          background: linear-gradient(90deg, #4f46e5 0%, #5eead4 100%);
          color: #fff;
          padding: 16px 32px 0 32px;
          font-size: 1.3rem;
          font-weight: 700;
          border-radius: 0 0 18px 18px;
          display: flex;
          flex-direction: column;
          box-shadow: 0 3px 20px #0002;
        }
        .nav-top {
          display: flex;
          align-items: center;
          justify-content: space-between;
        }
        .balance-modern {
          font-size: 1.09rem;
          font-weight: 600;
          letter-spacing: 0.3px;
          background: #fff;
          color: #22c55e;
          border-radius: 8px;
          padding: 3px 16px;
          margin-left: 20px;
        }
        .tab-bar {
          display: flex;
          gap: 8px;
          margin-top: 13px;
          margin-bottom: -4px;
          padding-left: 4px;
        }
        .tab-modern {
          padding: 7px 34px 8px 34px;
          background: transparent;
          color: #fff;
          border-radius: 13px 13px 0 0;
          font-size: 1.09rem;
          font-weight: 600;
          cursor: pointer;
          transition: background 0.18s, color 0.18s, box-shadow 0.13s;
          margin-right: 4px;
          position: relative;
          border-bottom: 2.7px solid transparent;
          outline: none;
        }
        .tab-modern:focus {
          box-shadow: 0 2px 8px #4f46e527;
        }
        .tab-modern.tab-active {
          background: #fff;
          color: #4f46e5;
          border-bottom: 2.7px solid #22d3ee;
          z-index: 1;
          box-shadow: 0 2px 12px #22d3ee14;
        }
        .inventory-bg {
          background: linear-gradient(120deg, #f8fafc 0%, #e0e7ff 100%);
          min-height: 100vh;
          padding-bottom: 54px;
        }
        .main-modern {
          max-width: 940px;
          margin: 44px auto 0 auto;
          padding: 0 22px;
        }
        .section-title {
          margin-top: 40px;
          margin-bottom: 12px;
          font-size: 1.42rem;
          font-weight: 600;
          color: #3730a3;
          letter-spacing: .3px;
        }
        .assets-flex {
          display: flex;
          flex-wrap: wrap;
          gap: 26px;
          margin-bottom: 36px;
        }
        .asset-card-modern {
          min-width: 240px;
          max-width: 270px;
          flex: 1 1 245px;
          background: #fff;
          border-radius: 16px;
          box-shadow: 0 4px 32px #4f46e50a;
          padding: 23px 22px 17px 22px;
          display: flex;
          flex-direction: column;
          align-items: flex-start;
          animation: fadeInUp .42s cubic-bezier(.21,1.04,.38,1);
          transition: transform 0.15s;
        }
        .asset-card-modern:hover {
          transform: translateY(-2px) scale(1.025);
          box-shadow: 0 8px 44px #4f46e524;
        }
        .asset-type-modern {
          font-size: 2.3rem;
          margin-bottom: 7px;
          opacity: 0.9;
        }
        .asset-title-modern {
          font-size: 1.15rem;
          font-weight: 700;
          margin-bottom: 7px;
          color: #3730a3;
        }
        .asset-detail-modern {
          color: #69718a;
          font-size: 1.01rem;
          margin-bottom: 3px;
        }
        .asset-id-modern {
          font-size: 0.95rem;
          color: #b0b2c3;
          margin-top: 7px;
        }
      `}</style>
      <div className="navbar-modern">
        <div className="nav-top">
          <span>Virtual Wallet</span>
          {address &&
          typeof address === "string" &&
          address.startsWith("0x") &&
          address.length === 42 ? (
            <span className="balance-modern">
              Balance: {parseFloat(balance).toFixed(4)} ETH
            </span>
          ) : null}
        </div>
        <div className="tab-bar">
          <Tab label="Inventory" to="/inventory" />
          <Tab label="Transfer" to="/transfer" />
        </div>
      </div>
      <div className="main-modern">
        <h1 style={{marginBottom: 16, marginTop: 30, fontWeight:700, color:"#262a43"}}>Your Properties</h1>
        <div style={{ fontSize: 15.5, color: "#666", marginBottom: 22 }}>
          View your owned houses and cars. Use the Transfer tab to send an item.
        </div>
        <div className="section-title">Houses</div>
        <div className="assets-flex">
          {houses.length === 0 && <span>No houses owned.</span>}
          {houses.map((nft) => (
            <div key={nft.tokenId} className="asset-card-modern">
              <div className="asset-type-modern">üè†</div>
              <div className="asset-title-modern">{nft.name}</div>
              <div className="asset-detail-modern">Worth: ${nft.value}</div>
              <div className="asset-detail-modern">Purchased: {nft.purchaseDate}</div>
              <div className="asset-detail-modern">Address: {nft.addr}</div>
              <div className="asset-id-modern">NFT ID: {nft.tokenId}</div>
            </div>
          ))}
        </div>
        <div className="section-title" style={{marginTop:30}}>Cars</div>
        <div className="assets-flex">
          {cars.length === 0 && <span>No cars owned.</span>}
          {cars.map((nft) => (
            <div key={nft.tokenId} className="asset-card-modern">
              <div className="asset-type-modern">üöó</div>
              <div className="asset-title-modern">{nft.name}</div>
              <div className="asset-detail-modern">Worth: ${nft.value}</div>
              <div className="asset-detail-modern">Purchased: {nft.purchaseDate}</div>
              <div className="asset-detail-modern">VIN: {nft.vin}</div>
              <div className="asset-id-modern">NFT ID: {nft.tokenId}</div>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
}
