import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import { getProvider, getEthBalance } from "../contractUtils";

// Map CNP and password to expected wallet addresses
const userMap = {
  "5000612430017": ["T12", "0xf39Fd6e51aad88F6F4ce6aB8827279cffFb92266"],
  "5000612430018": ["T13", "0x70997970C51812dc3A010C7d01b50e0d17dc79C8"],
  "5000612430019": ["T14", "0x3C44CdDdB6a900fa2b585dd299e03d12FA4293BC"],
  // Add more users as needed!
};

export default function Login() {
  const [cnp, setCnp] = useState("");
  const [password, setPassword] = useState("");
  const [error, setError] = useState("");
  const [balance, setBalance] = useState(null);
  const [address, setAddress] = useState(null);
  const navigate = useNavigate();

  const handleMetaMask = async (e) => {
    e.preventDefault();
    setError("");

    // Check CNP/password
    const entry = userMap[cnp];
    if (!entry || entry[0] !== password) {
      setError("Invalid CNP or password!");
      return;
    }
    const expectedAddress = entry[1].toLowerCase();

    try {
      const provider = await getProvider();
      const signer = provider.getSigner();
      const userAddress = (await signer.getAddress()).toLowerCase();

      if (userAddress !== expectedAddress) {
        setError(
          `For this login, please select wallet address: ${expectedAddress} in MetaMask! (Currently selected: ${userAddress})`
        );
        return;
      }

      setAddress(userAddress);
      setBalance(await getEthBalance(userAddress));
      navigate("/inventory");
    } catch {
      setError("Please install MetaMask and connect to the Hardhat network.");
    }
  };

  return (
    <div style={{ minHeight: "100vh", background: "#f8f9fa" }}>
      <div className="navbar">
        Virtual Wallet
        {address &&
        typeof address === "string" &&
        address.startsWith("0x") &&
        address.length === 42 &&
        balance !== null ? (
          <span style={{ float: "right", fontWeight: "400", fontSize: "18px" }}>
            Balance: <span style={{ color: "#2a9d44" }}>{parseFloat(balance).toFixed(4)} ETH</span>
          </span>
        ) : null}
      </div>
      <div
        className="main-content"
        style={{
          maxWidth: 430,
          marginTop: 80,
          marginBottom: 80,
          boxShadow: "0 8px 24px #0001",
        }}
      >
        <form
          onSubmit={handleMetaMask}
          style={{ display: "flex", flexDirection: "column", gap: 17 }}
        >
          <h2 style={{ margin: 0 }}>Log in</h2>
          <div style={{ color: "#888", fontSize: 15, marginBottom: 18 }}>
            Connect your wallet to continue.
          </div>
          <div>
            <label htmlFor="cnp">CNP</label>
            <input
              id="cnp"
              type="text"
              placeholder="Enter your CNP"
              value={cnp}
              onChange={(e) => setCnp(e.target.value)}
              style={{ width: "100%" }}
              autoComplete="username"
            />
          </div>
          <div>
            <label htmlFor="pw">Password</label>
            <input
              id="pw"
              type="password"
              placeholder="Enter your password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              style={{ width: "100%" }}
              autoComplete="current-password"
            />
          </div>
          <button
            className="button-main"
            type="submit"
            style={{ marginTop: 10, width: "100%" }}
          >
            Log in with MetaMask
          </button>
          {error && (
            <div style={{ color: "red", marginTop: 10 }}>
              {error}
              {error.includes("please select wallet address:") && (
                <div style={{ marginTop: 10 }}>
                  <button
                    style={{
                      background: "#f4f4f4",
                      color: "#0052cc",
                      border: "1px solid #ddd",
                      borderRadius: 5,
                      padding: "7px 16px",
                      marginTop: 8,
                      cursor: "pointer",
                      fontWeight: 600,
                    }}
                    onClick={async () => {
                      try {
                        await window.ethereum.request({ method: "eth_requestAccounts" });
                        window.location.reload();
                      } catch (e) {
                        alert("Could not open MetaMask! Please open it manually.");
                      }
                    }}
                  >
                    Switch Account in MetaMask
                  </button>
                </div>
              )}
            </div>
          )}
        </form>
        <div
          style={{
            display: "flex",
            justifyContent: "center",
            gap: 36,
            marginTop: 35,
            color: "#888",
            fontSize: 14,
          }}
        >
          <span>Contact Us</span>
          <span>Help</span>
          <span>Terms of Service</span>
        </div>
      </div>
    </div>
  );
}
