import React, { useEffect, useState } from "react";
import { getProvider, getPendingOffers, acceptOffer, getMyNFTs } from "../contractUtils";

export default function Offers({ user }) {
  const [address, setAddress] = useState(null);
  const [offers, setOffers] = useState([]);
  const [loading, setLoading] = useState(false);
  const [message, setMessage] = useState("");
  const [nfts, setNfts] = useState([]);

  useEffect(() => {
    async function fetchData() {
      const provider = await getProvider();
      const signer = provider.getSigner();
      const userAddress = await signer.getAddress();
      setAddress(userAddress);
      setOffers(await getPendingOffers(userAddress));
      setNfts(await getMyNFTs(userAddress));
    }
    fetchData();
  }, [user]);

  async function handleAcceptOffer(offer) {
    try {
      setLoading(true);
      await acceptOffer(offer.offerId, offer.price);
      setMessage(`Offer accepted! You paid ${offer.price} ETH and received the item.`);
      setOffers(await getPendingOffers(address));
      setNfts(await getMyNFTs(address));
    } catch (err) {
      setMessage("Error accepting offer: " + err.message);
    } finally {
      setLoading(false);
    }
  }

  return (
    <div className="casino-transfer-bg">
      <div className="casino-transfer-container">
        <h1 className="casino-main-title">Your Offers</h1>
        <div className="casino-transfer-desc" style={{marginBottom: 26}}>
          Below are all offers sent to you. Accept and pay to receive the asset!
        </div>
        <div className="casino-pending-box">
          <div style={{ color: "#777", fontSize: 15, marginBottom: 10 }}>
            If someone offered you an asset for ETH, you'll see it here.
          </div>
          {offers.length === 0 && (
            <span style={{ color: "#5a6078", fontWeight: 500 }}>
              No pending offers.
            </span>
          )}
          {offers.map((offer) => (
            <div key={offer.offerId} className="casino-pending-card">
              <div className="casino-pending-title">
                Offer for NFT #{offer.tokenId}
              </div>
              <div className="casino-pending-detail">
                <span style={{ color: "#b68908", fontWeight: 600 }}>Price:</span>{" "}
                <span style={{ color: "#fbbf24", fontWeight: 800 }}>{offer.price} ETH</span>
              </div>
              <div style={{ fontSize: 13 }}>
                From: <span style={{ color: "#555" }}>{offer.seller.slice(0, 8)}...{offer.seller.slice(-4)}</span>
              </div>
              <button
                className="casino-pending-btn"
                onClick={() => handleAcceptOffer(offer)}
                disabled={loading}
              >
                Accept & Pay
              </button>
            </div>
          ))}
        </div>
        {message && (
          <div
            className={
              "casino-transfer-message" +
              (message.startsWith("Error") ? " error" : "")
            }
          >
            {message}
          </div>
        )}
      </div>
      <style>{`
        .casino-transfer-bg {
          min-height: 100vh;
          background: radial-gradient(ellipse at 60% 0, #f9fafc 75%, #e0e7ff 100%);
          padding-bottom: 50px;
        }
        .casino-transfer-container {
          max-width: 1100px;
          margin: 44px auto 0 auto;
          padding: 0 18px;
        }
        .casino-main-title {
          font-size: 2.25rem;
          font-weight: 900;
          margin-bottom: 9px;
          color: #23126d;
          letter-spacing: 0.01em;
          text-shadow: 0 4px 18px #90cdf427, 0 1px 0 #fff;
        }
        .casino-transfer-desc {
          margin-bottom: 32px;
          color: #5a6078;
          font-size: 1.16rem;
        }
        .casino-pending-box {
          background: rgba(255,255,255,0.97);
          border-radius: 18px;
          box-shadow: 0 8px 32px #4f46e509, 0 2px 16px #0001;
          padding: 18px 18px 15px 18px;
          min-height: 120px;
          margin-top: 5px;
        }
        .casino-pending-card {
          background: #f9fafc;
          border-radius: 13px;
          padding: 15px 16px 13px 16px;
          margin-bottom: 12px;
          box-shadow: 0 2px 11px #fbbf2415;
        }
        .casino-pending-title {
          font-weight: 700; font-size: 1.09rem; color: #262a43; margin-bottom: 7px;
        }
        .casino-pending-detail {
          font-size: 1.04rem; margin-bottom: 6px; color: #b68908;
        }
        .casino-pending-btn {
          background: linear-gradient(90deg,#fbbf24,#6366f1 130%);
          color: #fff;
          border: none;
          font-weight: 700;
          padding: 8px 0;
          border-radius: 9px;
          font-size: 1.09rem;
          width: 100%;
          margin-top: 9px;
          box-shadow: 0 2px 7px #6366f128;
          cursor: pointer;
          transition: background .18s;
        }
        .casino-pending-btn:hover {
          background: linear-gradient(90deg,#6366f1,#fbbf24 160%);
        }
        .casino-transfer-message {
          margin-top: 30px;
          font-weight: 500;
          color: #3a3;
        }
        .casino-transfer-message.error {
          color: #e44;
        }
      `}</style>
    </div>
  );
}
