import React, { useEffect, useState } from "react";
import { getProvider, getMyNFTs, getEthBalance } from "../contractUtils";

export default function Inventory({ user, search, setSearch }) {
  const [address, setAddress] = useState(null);
  const [cars, setCars] = useState([]);
  const [houses, setHouses] = useState([]);
  const [balance, setBalance] = useState("0");

  useEffect(() => {
    async function fetchData() {
      const provider = await getProvider();
      const signer = provider.getSigner();
      const userAddress = await signer.getAddress();
      setAddress(userAddress);
      const nfts = await getMyNFTs(userAddress);
      setCars(nfts.filter(nft => nft.itemType === "car"));
      setHouses(nfts.filter(nft => nft.itemType === "house"));
      if (
        userAddress &&
        typeof userAddress === "string" &&
        userAddress.startsWith("0x") &&
        userAddress.length === 42
      ) {
        setBalance(await getEthBalance(userAddress));
      }
    }
    fetchData();
  }, [user]);

  // Filtering, derived from search
  const s = (search || "").trim().toLowerCase();
  const filteredHouses = !s
    ? houses
    : houses.filter(
        nft =>
          nft.name.toLowerCase().includes(s) ||
          (nft.addr && nft.addr.toLowerCase().includes(s))
      );
  const filteredCars = !s
    ? cars
    : cars.filter(
        nft =>
          nft.name.toLowerCase().includes(s) ||
          (nft.vin && nft.vin.toLowerCase().includes(s))
      );

  return (
    <div className="min-bg">
      <main className="min-inventory-content">
        <h1 className="min-title">Your Properties</h1>
        <div className="min-desc">
          View your owned houses and cars below. Use the Transfer tab to send an item.
        </div>
        <section>
          <div className="min-section-title">Houses</div>
          <div className="min-card-grid">
            {filteredHouses.length === 0 && (
              <span className="min-empty">No houses found.</span>
            )}
            {filteredHouses.map((nft) => (
              <div key={nft.tokenId} className="min-asset-card">
                <div className="min-asset-type">üè†</div>
                <div className="min-asset-title">{nft.name}</div>
                <div className="min-asset-detail">
                  Worth: <span className="min-money">${nft.value}</span>
                </div>
                <div className="min-asset-detail">Purchased: {nft.purchaseDate}</div>
                <div className="min-asset-detail">Address: {nft.addr}</div>
                <div className="min-asset-id">NFT ID: {nft.tokenId}</div>
              </div>
            ))}
          </div>
        </section>
        <div className="min-section-title" style={{ marginTop: 32 }}>
          Cars
        </div>
        <div className="min-card-grid">
          {filteredCars.length === 0 && (
            <span className="min-empty">No cars found.</span>
          )}
          {filteredCars.map((nft) => (
            <div key={nft.tokenId} className="min-asset-card">
              <div className="min-asset-type">üöó</div>
              <div className="min-asset-title">{nft.name}</div>
              <div className="min-asset-detail">
                Worth: <span className="min-money">${nft.value}</span>
              </div>
              <div className="min-asset-detail">Purchased: {nft.purchaseDate}</div>
              <div className="min-asset-detail">VIN: {nft.vin}</div>
              <div className="min-asset-id">NFT ID: {nft.tokenId}</div>
            </div>
          ))}
        </div>
      </main>
      <style>{`
        .min-bg {
          min-height: 100vh;
          background: #fafbfc;
          padding-bottom: 70px;
        }
        .min-inventory-content {
          max-width: 1050px;
          margin: 0 auto;
          margin-top: 42px;
          padding: 0 14px;
        }
        .min-title {
          font-size: 2.1rem;
          font-weight: 700;
          margin-bottom: 8px;
          color: #23283b;
        }
        .min-desc {
          margin-bottom: 30px;
          color: #4e5568;
          font-size: 1.09rem;
        }
        .min-section-title {
          font-size: 1.18rem;
          font-weight: 700;
          color: #226cf6;
          margin-bottom: 12px;
          letter-spacing: 0.01em;
        }
        .min-card-grid {
          display: grid;
          grid-template-columns: repeat(auto-fit, minmax(255px, 1fr));
          gap: 22px;
          margin-bottom: 34px;
        }
        .min-asset-card {
          background: #fff;
          border-radius: 13px;
          box-shadow: 0 2px 10px #eceff444;
          padding: 18px 20px 13px 20px;
          display: flex;
          flex-direction: column;
          align-items: flex-start;
          min-width: 210px;
          transition: box-shadow .16s, border .16s, transform .12s;
          border: 1.4px solid #f4f5f7;
          position: relative;
          overflow: hidden;
        }
        .min-asset-card:hover {
          border: 1.4px solid #226cf6;
          box-shadow: 0 8px 26px #226cf61a;
          transform: translateY(-2px) scale(1.016);
        }
        .min-asset-type {
          font-size: 2.05rem;
          margin-bottom: 6px;
        }
        .min-asset-title {
          font-size: 1.10rem;
          font-weight: 600;
          color: #23283b;
          margin-bottom: 6px;
        }
        .min-asset-detail {
          color: #4e5568;
          font-size: 1rem;
          margin-bottom: 2px;
        }
        .min-money {
          color: #226cf6;
          font-weight: 600;
          letter-spacing: 0.01em;
        }
        .min-asset-id {
          font-size: 0.96rem;
          color: #b2b6c7;
          margin-top: 7px;
          letter-spacing: 0.03em;
        }
        .min-empty {
          color: #b1a7c7;
          font-size: 1.04rem;
          font-weight: 500;
          padding: 16px 0 0 6px;
        }
        @media (max-width: 750px) {
          .min-card-grid {
            grid-template-columns: 1fr;
          }
          .min-inventory-content { padding: 0 2vw; }
        }
      `}</style>
    </div>
  );
}
