import React, { useEffect, useState } from "react";
import {
  getProvider,
  getMyNFTs,
  createBatchOffers, // <-- use this!
  getEthBalance,
} from "../contractUtils";

export default function Transfer({ user }) {
  const [address, setAddress] = useState(null);
  const [nfts, setNfts] = useState([]);
  const [selected, setSelected] = useState([]); // Array for multi-select
  const [to, setTo] = useState("");
  const [amount, setAmount] = useState("");
  const [message, setMessage] = useState("");
  const [loading, setLoading] = useState(false);

  useEffect(() => {
    async function fetchData() {
      const provider = await getProvider();
      const signer = provider.getSigner();
      const userAddress = await signer.getAddress();
      setAddress(userAddress);
      setNfts(await getMyNFTs(userAddress));
    }
    fetchData();
  }, [user]);

  // Handle selection toggle
  function toggleSelected(tokenId) {
    setSelected((prev) =>
      prev.includes(tokenId)
        ? prev.filter((id) => id !== tokenId)
        : [...prev, tokenId]
    );
  }

  // Use batch offer
  async function handleCreateOffer(e) {
    e.preventDefault();
    if (!selected.length || !to || !amount) {
      setMessage("Select one or more assets, recipient, and amount.");
      return;
    }
    setMessage("");
    setLoading(true);
    try {
      await Promise.all(selected.map(tokenId => createOffer(tokenId, to, amount)));
      setMessage(
        selected.length > 1
          ? `Offers created for ${selected.length} items!`
          : "Offer created!"
      );
      setTo("");
      setAmount("");
      setSelected([]);
    } catch (err) {
      setMessage("Error: " + (err?.message || err));
    } finally {
      setLoading(false);
    }
  }

  return (
    <div className="casino-transfer-bg">
      <div className="casino-transfer-container">
        <h1 className="casino-main-title">Send Trade Offer</h1>
        <div className="casino-transfer-desc">
          Select <b>one or more assets</b>, enter recipient wallet, and set price (ETH) to create offers.<br />
          Recipient must accept and pay for trade to complete!
        </div>
        <div className="casino-asset-list">
          {nfts.map((nft) => (
            <div
              key={nft.tokenId}
              className={
                "casino-asset-card" +
                (selected.includes(nft.tokenId) ? " casino-selected" : "")
              }
              onClick={() => toggleSelected(nft.tokenId)}
            >
              <div className="casino-asset-type">
                {nft.itemType === "car" ? "üöó" : "üè†"}
              </div>
              <div className="casino-asset-title">{nft.name}</div>
              <div className="casino-asset-detail">
                {nft.itemType === "car"
                  ? `VIN: ${nft.vin}`
                  : `Address: ${nft.addr}`}
              </div>
              <div className="casino-asset-id">NFT ID: {nft.tokenId}</div>
              {selected.includes(nft.tokenId) && (
                <span className="casino-multiselect-check">‚úì</span>
              )}
            </div>
          ))}
        </div>
        <form onSubmit={handleCreateOffer} className="casino-transfer-form">
          <label htmlFor="to" className="casino-label">
            Recipient Wallet Address
          </label>
          <input
            id="to"
            className="casino-input"
            type="text"
            placeholder="0x..."
            value={to}
            onChange={(e) => setTo(e.target.value)}
            disabled={loading}
          />
          <label htmlFor="amount" className="casino-label">
            Amount (ETH) <span style={{ color: "#aaa", fontSize: 13 }}>(for each item)</span>
          </label>
          <input
            id="amount"
            className="casino-input"
            type="number"
            step="0.0001"
            min="0"
            placeholder="Amount in ETH"
            value={amount}
            onChange={(e) => setAmount(e.target.value)}
            disabled={loading}
          />
          <button className="casino-button-main" type="submit" disabled={loading}>
            {loading
              ? `Processing${selected.length > 1 ? ` ${selected.length}` : ""}...`
              : selected.length > 1
              ? `Create ${selected.length} Offers`
              : "Create Offer"}
          </button>
        </form>
        {message && (
          <div
            className={
              "casino-transfer-message" +
              (message.startsWith("Error") ? " error" : "")
            }
          >
            {message}
          </div>
        )}
      </div>
      <style>{`
        /* ... keep your casino styles, unchanged ... */
        .casino-transfer-bg {
          min-height: 100vh;
          background: radial-gradient(ellipse at 60% 0, #f9fafc 75%, #e0e7ff 100%);
          padding-bottom: 50px;
        }
        .casino-transfer-container {
          max-width: 1100px;
          margin: 44px auto 0 auto;
          padding: 0 18px;
        }
        .casino-main-title {
          font-size: 2.25rem;
          font-weight: 900;
          margin-bottom: 9px;
          color: #23126d;
          letter-spacing: 0.01em;
          text-shadow: 0 4px 18px #90cdf427, 0 1px 0 #fff;
        }
        .casino-transfer-desc {
          margin-bottom: 32px;
          color: #5a6078;
          font-size: 1.16rem;
        }
        .casino-asset-list {
          display: flex;
          flex-wrap: wrap;
          gap: 28px;
          margin-bottom: 28px;
        }
        .casino-asset-card {
          background: rgba(255,255,255,0.91);
          border-radius: 19px;
          box-shadow: 0 6px 24px #6f68f13a, 0 1.5px 9px #0001;
          padding: 22px 23px 14px 22px;
          display: flex;
          flex-direction: column;
          align-items: flex-start;
          min-width: 215px;
          max-width: 260px;
          transition: box-shadow .17s, border .17s, transform .13s;
          border: 2.5px solid transparent;
          position: relative;
          cursor: pointer;
        }
        .casino-asset-card:hover {
          border: 2.5px solid #fbbf24;
          box-shadow: 0 12px 40px #ffe66a37, 0 2px 18px #6366f123;
          transform: translateY(-4px) scale(1.022);
        }
        .casino-selected {
          border: 2.5px solid #4980f6 !important;
          box-shadow: 0 8px 36px #7eb6ff4e, 0 2px 14px #6366f115;
          background: #f3f8ff !important;
        }
        .casino-multiselect-check {
          position: absolute;
          top: 11px; right: 14px;
          background: #4f46e5;
          color: #fff;
          border-radius: 100%;
          font-size: 1.3rem;
          font-weight: 700;
          width: 26px; height: 26px;
          display: flex; align-items: center; justify-content: center;
          box-shadow: 0 2px 8px #4f46e522;
          border: 2.5px solid #fbbf24;
          z-index: 2;
        }
        .casino-asset-type {
          font-size: 2.15rem;
          margin-bottom: 7px;
          text-shadow: 0 2px 12px #facc1521;
        }
        .casino-asset-title {
          font-size: 1.13rem;
          font-weight: 700;
          color: #23126d;
          margin-bottom: 7px;
          letter-spacing: 0.03em;
          animation: shimmer-casino 2.4s infinite linear;
          background: linear-gradient(90deg,#ffe066,#6366f1 50%,#ffe066 100%);
          background-size: 200% 100%;
          -webkit-background-clip: text;
          -webkit-text-fill-color: transparent;
        }
        @keyframes shimmer-casino {
          0% { background-position: 120% 0; }
          100% { background-position: -120% 0; }
        }
        .casino-asset-detail {
          color: #69718a;
          font-size: 1.04rem;
          margin-bottom: 3px;
        }
        .casino-asset-id {
          font-size: 0.99rem;
          color: #b0b2c3;
          margin-top: 7px;
        }
        .casino-transfer-form {
          margin: 0 auto;
          max-width: 350px;
          display: flex; flex-direction: column; gap: 10px;
          background: rgba(255,255,255,0.97);
          padding: 23px 24px 18px 24px;
          border-radius: 16px;
          box-shadow: 0 3px 18px #4f46e513;
          margin-bottom: 34px;
        }
        .casino-label {
          font-weight: 600;
          margin-bottom: 2px;
          color: #3730a3;
        }
        .casino-input {
          padding: 8px 12px;
          border-radius: 9px;
          border: 1px solid #d1d5db;
          font-size: 1.09rem;
          margin-bottom: 7px;
          background: #fafcff;
          outline: none;
          transition: border .13s;
        }
        .casino-input:focus {
          border: 1.7px solid #6366f1;
          background: #fff;
        }
        .casino-button-main {
          background: linear-gradient(90deg,#6366f1,#fbbf24 120%);
          color: #fff;
          border: none;
          font-weight: 700;
          padding: 12px 0;
          border-radius: 11px;
          font-size: 1.15rem;
          margin-top: 9px;
          box-shadow: 0 2px 9px #6366f129;
          cursor: pointer;
          transition: background .18s,box-shadow .18s;
        }
        .casino-button-main:disabled {
          opacity: 0.66;
          cursor: not-allowed;
        }
        .casino-button-main:hover {
          background: linear-gradient(90deg,#4f46e5,#fbbf24 180%);
          box-shadow: 0 4px 20px #fbbf2429;
        }
        .casino-transfer-message {
          margin-top: 30px;
          font-weight: 500;
          color: #3a3;
        }
        .casino-transfer-message.error {
          color: #e44;
        }
        @media (max-width: 750px) {
          .casino-asset-list {
            flex-direction: column;
          }
          .casino-transfer-container { padding: 0 2vw; }
        }
      `}</style>
    </div>
  );
}
