import React, { useState } from "react";
import { Link, useLocation } from "react-router-dom";

// Avatar component using Jazzicon-style fallback
function Avatar({ address, size = 36 }) {
  if (!address)
    return (
      <div
        style={{
          width: size,
          height: size,
          borderRadius: "50%",
          background: "#bae6fd",
          display: "flex",
          alignItems: "center",
          justifyContent: "center",
          fontWeight: 900,
          fontSize: size * 0.6,
          color: "#0891b2",
          border: "2.5px solid #e0e7ef",
        }}
      >
        ?
      </div>
    );
  const abbr = address.slice(2, 4).toUpperCase() + address.slice(-2).toUpperCase();
  return (
    <div
      style={{
        width: size,
        height: size,
        borderRadius: "50%",
        background: "#6366f1",
        display: "flex",
        alignItems: "center",
        justifyContent: "center",
        color: "#fff",
        fontWeight: 900,
        fontSize: size * 0.57,
        letterSpacing: 1,
        border: "2.5px solid #e0e7ef",
        userSelect: "none",
      }}
      title={address}
    >
      {abbr}
    </div>
  );
}

export default function Navbar({
  balance,
  onLogout,
  search,
  setSearch,
  handleSearch,
  user,
}) {
  const location = useLocation();
  const [profileOpen, setProfileOpen] = useState(false);

  return (
    <header className="navbar-modern">
      <div className="nav-top">
        <div className="brand-1312">
          Rares<span className="dot">.</span>
        </div>
        <div className="tab-bar">
          <Link to="/inventory" className={`tab-modern${location.pathname === "/inventory" ? " tab-active" : ""}`}>Inventory</Link>
          <Link to="/transfer" className={`tab-modern${location.pathname === "/transfer" ? " tab-active" : ""}`}>Transfer</Link>
          <Link to="/offers" className={`tab-modern${location.pathname === "/offers" ? " tab-active" : ""}`}>Offers</Link>
          <Link to="/about" className={`tab-modern${location.pathname === "/about" ? " tab-active" : ""}`}>About</Link>
        </div>
      </div>
      <div style={{ display: "flex", alignItems: "center", marginTop: 18, position: "relative" }}>
        <form className="search-modern" onSubmit={handleSearch} autoComplete="off">
          <input
            type="text"
            className="search-input-modern"
            placeholder="Searchâ€¦"
            value={search}
            onChange={e => setSearch(e.target.value)}
          />
          <button className="search-btn-modern" type="submit">
            Search
          </button>
        </form>
        <div className="account-modern" title="Wallet balance">
          <span className="coin-emoji" role="img" aria-label="coin">ðŸª™</span>
          <span style={{ fontWeight: 900 }}>{balance ? parseFloat(balance).toFixed(4) : "0.0000"}</span>
          <span style={{ color: "#8891b0", fontWeight: 700, marginLeft: 5 }}>ETH</span>
        </div>
        {/* Avatar/Profile Dropdown */}
        <div
          style={{ position: "relative", marginLeft: 17, cursor: "pointer" }}
          tabIndex={0}
          onClick={() => setProfileOpen(v => !v)}
          onBlur={() => setProfileOpen(false)}
        >
          <Avatar address={user?.address} />
          <span style={{ display: "inline-block", marginLeft: 6, fontSize: 17, color: "#94a3b8", verticalAlign: "middle" }}>â–¼</span>
          {profileOpen && (
            <div className="profile-dropdown">
              <div style={{ display: "flex", alignItems: "center", gap: 12, marginBottom: 8 }}>
                <Avatar address={user?.address} size={40} />
                <div style={{ fontWeight: 800, color: "#475569", fontSize: "1.08rem" }}>
                  {user?.address
                    ? user.address.slice(0, 6) + "..." + user.address.slice(-4)
                    : "No Wallet"}
                </div>
              </div>
              <div style={{ fontWeight: 600, fontSize: "1.01rem", marginBottom: 4 }}>
                Balance: <span style={{ color: "#0ea5e9", fontWeight: 900 }}>{balance ? parseFloat(balance).toFixed(4) : "0.0000"} ETH</span>
              </div>
              <button className="logout-modern" onClick={onLogout} style={{ width: "100%", marginTop: 10 }}>
                Logout
              </button>
            </div>
          )}
        </div>
      </div>
      <style>{`
        @import url('https://fonts.googleapis.com/css2?family=Inter:wght@400;700;900&display=swap');
        .navbar-modern {
          width: 100%;
          background: #fff;
          color: #18181b;
          padding: 22px 50px 0 50px;
          font-family: 'Inter', system-ui, sans-serif;
          border-bottom: 1.5px solid #ececf0;
          box-shadow: 0 2px 16px #bcd0ff16;
        }
        .nav-top {
          display: flex;
          align-items: center;
          justify-content: space-between;
        }
        .brand-1312 {
          font-size: 2rem;
          font-weight: 900;
          color: #20212a;
          letter-spacing: 0.01em;
          user-select: none;
        }
        .dot {
          color: #262cff;
          font-size: 1.8rem;
        }
        .tab-bar {
          display: flex;
          align-items: center;
          gap: 22px;
        }
        .tab-modern {
          padding: 8px 24px;
          background: transparent;
          color: #757793;
          border-radius: 13px 13px 0 0;
          font-size: 1.1rem;
          font-weight: 700;
          cursor: pointer;
          border-bottom: 2.7px solid transparent;
          transition: background 0.18s, color 0.18s;
          outline: none;
        }
        .tab-modern.tab-active {
          background: #f3f6ff;
          color: #262cff;
          border-bottom: 2.7px solid #262cff;
          z-index: 1;
          box-shadow: 0 2px 10px #bbc6fd33;
        }
        .search-modern {
          margin: 0 12px;
          display: flex;
          align-items: center;
          gap: 6px;
        }
        .search-input-modern {
          border: 1px solid #e2e8f0;
          border-radius: 7px;
          font-size: 1rem;
          padding: 4px 10px;
          outline: none;
          background: #f4f6fb;
        }
        .search-input-modern:focus {
          border: 1.5px solid #262cff;
          background: #fff;
        }
        .search-btn-modern {
          background: #262cff;
          color: #fff;
          font-weight: 700;
          border: none;
          border-radius: 7px;
          font-size: 1rem;
          padding: 7px 15px;
          cursor: pointer;
          transition: background .14s;
        }
        .search-btn-modern:hover {
          background: #0a1675;
        }
        .account-modern {
          display: flex;
          align-items: center;
          background: #f4f6fb;
          color: #181a23;
          border-radius: 26px;
          padding: 6px 15px 6px 12px;
          font-size: 1.11rem;
          font-weight: 700;
          border: 1px solid #ececf0;
          gap: 7px;
          min-width: 110px;
          margin-right: 2px;
          box-shadow: 0 1.5px 10px #e7eaf233;
        }
        .coin-emoji {
          font-size: 1.36em;
          margin-right: 2px;
          animation: coin-spin 1.3s linear infinite;
          filter: drop-shadow(0 1px 4px #0001);
          vertical-align: middle;
          display: inline-block;
          transform-origin: 60% 58%;
        }
        @keyframes coin-spin {
          0% { transform: rotateY(0deg);}
          65% { transform: rotateY(380deg);}
          100% { transform: rotateY(360deg);}
        }
        .logout-modern {
          background: none;
          border: none;
          color: #222cff;
          font-weight: 700;
          font-size: 1.07rem;
          cursor: pointer;
          padding: 6px 0 0 12px;
          transition: color .13s;
        }
        .logout-modern:hover {
          color: #e11d48;
        }
        /* Profile dropdown */
        .profile-dropdown {
          position: absolute;
          right: 0;
          top: 48px;
          min-width: 220px;
          background: #fff;
          border-radius: 15px;
          box-shadow: 0 6px 32px #64748b28, 0 1.5px 9px #0001;
          padding: 16px 18px 13px 18px;
          z-index: 30;
          display: flex;
          flex-direction: column;
          gap: 2px;
          border: 1.2px solid #e2e8f0;
          animation: profileDrop .18s cubic-bezier(.35,1.42,.6,1.03);
        }
        @keyframes profileDrop {
          from { opacity: 0; transform: translateY(-10px) scale(.95);}
          to { opacity: 1; transform: translateY(0) scale(1);}
        }
        @media (max-width: 750px) {
          .navbar-modern { padding: 16px 4vw 0 4vw;}
          .brand-1312 { font-size: 1.3rem;}
          .tab-modern { padding: 6px 10px; font-size: 1rem;}
        }
        @media (max-width: 500px) {
          .brand-1312 { font-size: 1.06rem;}
          .tab-bar { gap: 7px;}
        }
      `}</style>
    </header>
  );
}
