import React, { useEffect, useState } from "react";
import {
  getProvider,
  getMyNFTs,
  createOffer,
  getPendingOffers,
  acceptOffer,
  getEthBalance,
} from "../contractUtils";
import { useNavigate, useLocation } from "react-router-dom";

export default function Transfer() {
  const [address, setAddress] = useState(null);
  const [nfts, setNfts] = useState([]);
  const [selected, setSelected] = useState(null);
  const [to, setTo] = useState("");
  const [amount, setAmount] = useState("");
  const [message, setMessage] = useState("");
  const [offers, setOffers] = useState([]);
  const [balance, setBalance] = useState("0");
  const [loading, setLoading] = useState(false);
  const navigate = useNavigate();
  const location = useLocation();

  useEffect(() => {
    async function fetchData() {
      const provider = await getProvider();
      const signer = provider.getSigner();
      const userAddress = await signer.getAddress();
      setAddress(userAddress);
      setNfts(await getMyNFTs(userAddress));
      setOffers(await getPendingOffers(userAddress));
      if (
        userAddress &&
        typeof userAddress === "string" &&
        userAddress.startsWith("0x") &&
        userAddress.length === 42
      ) {
        setBalance(await getEthBalance(userAddress));
      }
    }
    fetchData();

    const interval = setInterval(async () => {
      if (
        address &&
        typeof address === "string" &&
        address.startsWith("0x") &&
        address.length === 42
      ) {
        setBalance(await getEthBalance(address));
      }
    }, 10000);
    return () => clearInterval(interval);
  }, [address]);

  // --- Modern Tab NavBar ---
  function Tab({ label, to }) {
    const active = location.pathname === to;
    return (
      <div
        className={`tab-modern${active ? " tab-active" : ""}`}
        onClick={() => navigate(to)}
        tabIndex={0}
      >
        {label}
      </div>
    );
  }

  // ...rest of your Transfer code stays the same, including styling...

  return (
    <div className="transfer-bg">
      <style>{`
        /* Paste the same navbar/tab styles from Inventory.js */
        .navbar-modern {
          width: 100%;
          background: linear-gradient(90deg, #4f46e5 0%, #5eead4 100%);
          color: #fff;
          padding: 16px 32px 0 32px;
          font-size: 1.3rem;
          font-weight: 700;
          border-radius: 0 0 18px 18px;
          display: flex;
          flex-direction: column;
          box-shadow: 0 3px 20px #0002;
        }
        .nav-top {
          display: flex;
          align-items: center;
          justify-content: space-between;
        }
        .balance-modern {
          font-size: 1.09rem;
          font-weight: 600;
          background: #fff;
          color: #22c55e;
          border-radius: 8px;
          padding: 3px 16px;
          margin-left: 20px;
        }
        .tab-bar {
          display: flex;
          gap: 8px;
          margin-top: 13px;
          margin-bottom: -4px;
          padding-left: 4px;
        }
        .tab-modern {
          padding: 7px 34px 8px 34px;
          background: transparent;
          color: #fff;
          border-radius: 13px 13px 0 0;
          font-size: 1.09rem;
          font-weight: 600;
          cursor: pointer;
          transition: background 0.18s, color 0.18s, box-shadow 0.13s;
          margin-right: 4px;
          position: relative;
          border-bottom: 2.7px solid transparent;
          outline: none;
        }
        .tab-modern:focus {
          box-shadow: 0 2px 8px #4f46e527;
        }
        .tab-modern.tab-active {
          background: #fff;
          color: #4f46e5;
          border-bottom: 2.7px solid #22d3ee;
          z-index: 1;
          box-shadow: 0 2px 12px #22d3ee14;
        }
        /* ...the rest of your modern transfer CSS... */
        .transfer-bg {
          background: linear-gradient(120deg, #f8fafc 0%, #e0e7ff 100%);
          min-height: 100vh;
          padding-bottom: 54px;
        }
        /* ... keep your rest of the styles for asset cards, buttons, etc. ... */
      `}</style>
      <div className="navbar-modern">
        <div className="nav-top">
          <span>Virtual Wallet</span>
          {address &&
          typeof address === "string" &&
          address.startsWith("0x") &&
          address.length === 42 ? (
            <span className="balance-modern">
              Balance: {parseFloat(balance).toFixed(4)} ETH
            </span>
          ) : null}
        </div>
        <div className="tab-bar">
          <Tab label="Inventory" to="/inventory" />
          <Tab label="Transfer" to="/transfer" />
        </div>
      </div>
      {/* ...rest of your Transfer page code (as above, with all cards etc.) ... */}
      {/* ... */}
    </div>
  );
}
