import React, { useEffect, useState } from "react";
import { getProvider, getMyNFTs, transferNFT } from "../contractUtils";
import { useNavigate } from "react-router-dom";

export default function Transfer() {
  const [address, setAddress] = useState("");
  const [nfts, setNfts] = useState([]);
  const [selected, setSelected] = useState(null);
  const [to, setTo] = useState("");
  const [message, setMessage] = useState("");
  const navigate = useNavigate();

  useEffect(() => {
    async function fetchData() {
      const provider = await getProvider();
      const signer = provider.getSigner();
      const userAddress = await signer.getAddress();
      setAddress(userAddress);
      setNfts(await getMyNFTs(userAddress));
    }
    fetchData();
  }, []);

  async function handleTransfer(e) {
    e.preventDefault();
    if (!selected || !to) {
      setMessage("Please select an item and enter a recipient address.");
      return;
    }
    try {
      await transferNFT(selected, to);
      setMessage("Transferred successfully!");
      setNfts(nfts.filter(nft => nft.tokenId !== selected));
    } catch (err) {
      setMessage("Error transferring: " + err.message);
    }
  }

  return (
    <div>
      <div className="navbar">Virtual Wallet</div>
      <div className="main-content">
        <h1 style={{marginTop:0}}>Transfer Asset</h1>
        <div style={{marginBottom:32, color:"#666", fontSize:15}}>Select the asset you want to transfer and enter the recipient's wallet address.</div>
        <div style={{display:"flex",gap:40,flexWrap:"wrap",marginBottom:32,justifyContent:"center"}}>
          {nfts.map(nft => (
            <div
              key={nft.tokenId}
              className="asset-card"
              onClick={() => setSelected(nft.tokenId)}
              style={{
                border: selected === nft.tokenId ? "2.5px solid #0052cc" : undefined,
                boxShadow: selected === nft.tokenId ? "0 4px 24px #0052cc14" : undefined,
                cursor: "pointer",
                background: selected === nft.tokenId ? "#f0f7ff" : undefined
              }}
            >
              <div className="asset-type">{nft.itemType === "car" ? "üöó" : "üè†"}</div>
              <div className="asset-title">{nft.name}</div>
              <div className="asset-detail">{nft.itemType === "car" ? `VIN: ${nft.vin}` : `Address: ${nft.addr}`}</div>
              <div style={{ fontSize: 12, color: "#888" }}>NFT ID: {nft.tokenId}</div>
            </div>
          ))}
        </div>
        <form onSubmit={handleTransfer} style={{margin:"0 auto",maxWidth:340,display:"flex",flexDirection:"column",gap:15}}>
          <label htmlFor="to">Recipient Wallet Address</label>
          <input
            id="to"
            type="text"
            placeholder="0x..."
            value={to}
            onChange={e => setTo(e.target.value)}
            style={{width:"100%"}}
          />
          <button className="button-main" type="submit">Transfer</button>
        </form>
        {message && <div style={{marginTop:22,fontWeight:500,color: message.includes("success")?"#3a3":"#e44"}}>{message}</div>}
        <div style={{marginTop:44}}>
          <button className="button-main" onClick={() => navigate("/inventory")}>Back to Inventory</button>
        </div>
      </div>
    </div>
  );
}
