import React from "react";
import { Link, useLocation } from "react-router-dom";

export default function Navbar({ balance, onLogout, search, setSearch, handleSearch }) {
  const location = useLocation();

  return (
    <div className="navbar-bg">
      <nav className="navbar">
        <div className="navbar-left">
          <span className="navbar-logo">Rares's project</span>
          <Link to="/inventory" className={location.pathname === "/inventory" ? "navbar-link active" : "navbar-link"}>Inventory</Link>
          <Link to="/transfer" className={location.pathname === "/transfer" ? "navbar-link active" : "navbar-link"}>Transfer</Link>
          <Link to="/about" className={location.pathname === "/about" ? "navbar-link active" : "navbar-link"}>About</Link>
        </div>
        <form className="navbar-search" onSubmit={handleSearch}>
          <input
            type="text"
            placeholder="Search..."
            value={search}
            onChange={e => setSearch(e.target.value)}
          />
          <button type="submit">SEARCH</button>
        </form>
        <div className="navbar-right">
          <div className="navbar-balance-chip">
            <span className="coin-emoji" role="img" aria-label="coin">ðŸª™</span>
            {balance ? (
              <span style={{fontWeight:800}}>
                {parseFloat(balance).toFixed(4)} ETH
              </span>
            ) : (
              <span style={{opacity:0.6}}>0.0000 ETH</span>
            )}
          </div>
          <button className="navbar-logout" onClick={onLogout}>Log Out</button>
        </div>
      </nav>
      <style>{`
        .navbar-bg {
          width: 100%;
          background: #f4f4f7;
          border-bottom: 1.2px solid #e5e7eb;
          box-shadow: 0 1.5px 12px #6366f103;
        }
        .navbar {
          display: flex;
          justify-content: space-between;
          align-items: center;
          max-width: 1200px;
          margin: 0 auto;
          padding: 15px 28px;
        }
        .navbar-logo {
          font-size: 2.1rem;
          font-weight: 700;
          color: #3730a3;
          margin-right: 40px;
          letter-spacing: 0.15em;
        }
        .navbar-left {
          display: flex;
          align-items: center;
          gap: 12px;
        }
        .navbar-link {
          font-size: 1.08rem;
          font-weight: 600;
          color: #6b7280;
          text-decoration: none;
          margin-right: 17px;
          padding-bottom: 3px;
          border-bottom: 2px solid transparent;
          transition: color .13s, border .13s;
        }
        .navbar-link.active {
          color: #6366f1;
          border-bottom: 2.5px solid #6366f1;
        }
        .navbar-search {
          display: flex;
          align-items: center;
          gap: 8px;
        }
        .navbar-search input {
          border: 1.3px solid #d1d5db;
          border-radius: 7px;
          font-size: 16px;
          padding: 7px 12px;
          background: #fff;
        }
        .navbar-search button {
          background: linear-gradient(90deg,#6366f1 60%,#5eead4 140%);
          color: #fff;
          font-weight: 700;
          border: none;
          border-radius: 7px;
          font-size: 15px;
          padding: 7px 16px;
          cursor: pointer;
          box-shadow: 0 2px 9px #6366f125;
        }
        .navbar-search button:hover {
          background: linear-gradient(90deg,#6366f1 100%,#06b6d4 200%);
        }
        .navbar-right {
          display: flex;
          align-items: center;
          gap: 17px;
        }
        .navbar-balance-chip {
          background: linear-gradient(90deg, #fbbf24 70%, #facc15 130%);
          color: #fff;
          border-radius: 50px;
          padding: 10px 26px;
          font-size: 1.19rem;
          font-weight: 700;
          display: flex;
          align-items: center;
          box-shadow: 0 3px 16px #facc1530;
          margin-right: 3px;
          letter-spacing: 0.01em;
        }
        .coin-emoji {
          display: inline-block;
          font-size: 1.45em;
          margin-right: 6px;
          /* Spin animation */
          animation: coin-spin 1.5s linear infinite;
          transform-origin: 55% 60%;
          filter: drop-shadow(0 2px 4px #0002);
        }
        @keyframes coin-spin {
          0% { transform: rotateY(0deg);}
          70% { transform: rotateY(360deg);}
          100% { transform: rotateY(360deg);}
        }
        .navbar-logout {
          background: none;
          border: none;
          color: #6366f1;
          font-weight: 700;
          font-size: 1.08rem;
          cursor: pointer;
          padding: 0 0 0 7px;
          transition: color .13s;
        }
        .navbar-logout:hover {
          color: #e11d48;
        }
      `}</style>
    </div>
  );
}
