import React, { useState } from "react";
import { BrowserRouter as Router, Routes, Route, Navigate } from "react-router-dom";
import Login from "./components/Login";
import Inventory from "./components/Inventory";
import Transfer from "./components/Transfer";
import About from "./components/About"; // if you have an About page/component
import Navbar from "./components/Navbar"; // if you use a modern navbar (optional)

// Optional: Global state for user
function App() {
  const [user, setUser] = useState(null);

  return (
    <Router>
      {user && <Navbar user={user} setUser={setUser} />} {/* Hide navbar if not logged in */}
      <Routes>
        <Route path="/" element={<Login onLogin={setUser} />} />
        <Route
          path="/inventory"
          element={user ? <Inventory user={user} /> : <Navigate to="/" />}
        />
        <Route
          path="/transfer"
          element={user ? <Transfer user={user} /> : <Navigate to="/" />}
        />
        <Route path="/about" element={<About />} />
        {/* Fallback to login */}
        <Route path="*" element={<Navigate to="/" />} />
      </Routes>
    </Router>
  );
}
export default App;
