// src/components/Transfer.js
import React, { useEffect, useState } from "react";
import {
  getProvider,
  getMyNFTs,
  createOffer,
  getPendingOffers,
  acceptOffer,
  getEthBalance,
} from "../contractUtils";
import Navbar from "./Navbar";
import { useNavigate, useLocation } from "react-router-dom";

export default function Transfer() {
  const [address, setAddress] = useState(null);
  const [nfts, setNfts] = useState([]);
  const [filteredNfts, setFilteredNfts] = useState([]);
  const [selected, setSelected] = useState(null);
  const [to, setTo] = useState("");
  const [amount, setAmount] = useState("");
  const [message, setMessage] = useState("");
  const [offers, setOffers] = useState([]);
  const [balance, setBalance] = useState("0");
  const [loading, setLoading] = useState(false);
  const navigate = useNavigate();

  // Fetch data on mount and when address changes
  useEffect(() => {
    async function fetchData() {
      const provider = await getProvider();
      const signer = provider.getSigner();
      const userAddress = await signer.getAddress();
      setAddress(userAddress);
      const allNfts = await getMyNFTs(userAddress);
      setNfts(allNfts);
      setFilteredNfts(allNfts);
      setOffers(await getPendingOffers(userAddress));
      if (userAddress && typeof userAddress === "string" && userAddress.startsWith("0x") && userAddress.length === 42) {
        setBalance(await getEthBalance(userAddress));
      }
    }
    fetchData();

    const interval = setInterval(async () => {
      if (address && typeof address === "string" && address.startsWith("0x") && address.length === 42) {
        setBalance(await getEthBalance(address));
      }
    }, 10000);
    return () => clearInterval(interval);
  }, [address]);

  // Search by asset name
  function handleSearch(query) {
    if (!query) return setFilteredNfts(nfts);
    setFilteredNfts(
      nfts.filter((nft) =>
        nft.name.toLowerCase().includes(query.toLowerCase())
      )
    );
  }

  async function handleCreateOffer(e) {
    e.preventDefault();
    if (!selected || !to || !amount) {
      setMessage("Select an asset, recipient, and amount.");
      return;
    }
    try {
      setLoading(true);
      await createOffer(selected, to, amount);
      setMessage("Offer created! Buyer will see it in their pending offers.");
      setTo("");
      setAmount("");
      setSelected(null);
    } catch (err) {
      setMessage("Error: " + err.message);
    } finally {
      setLoading(false);
    }
  }

  async function handleAcceptOffer(offer) {
    try {
      setLoading(true);
      await acceptOffer(offer.offerId, offer.price);
      setMessage(`Offer accepted! You paid ${offer.price} ETH and received the item.`);
      setOffers(await getPendingOffers(address));
      setNfts(await getMyNFTs(address));
      setFilteredNfts(await getMyNFTs(address));
      if (address && typeof address === "string" && address.startsWith("0x") && address.length === 42) {
        setBalance(await getEthBalance(address));
      }
    } catch (err) {
      setMessage("Error accepting offer: " + err.message);
    } finally {
      setLoading(false);
    }
  }

  function handleLogout() {
    window.location.href = "/";
  }

  return (
    <div className="transfer-bg">
      <Navbar
        onSearch={handleSearch}
        onLogout={handleLogout}
        walletAddress={address}
      />
      <div className="main-modern">
        <h1 style={{ marginBottom: 18, fontWeight: 700, color: "#222" }}>Send Trade Offer</h1>
        <div style={{ marginBottom: 18, color: "#666", fontSize: 15 }}>
          Select an asset, enter recipient wallet, and set price (ETH) to create an offer.<br />
          Recipient must accept and pay for trade to complete!
        </div>
        {/* Asset cards */}
        <div className="assets-flex">
          {filteredNfts.map((nft) => (
            <div
              key={nft.tokenId}
              className={
                "asset-card-modern" + (selected === nft.tokenId ? " selected" : "")
              }
              onClick={() => setSelected(nft.tokenId)}
            >
              <div className="asset-type-modern">
                {nft.itemType === "car" ? "🚗" : "🏠"}
              </div>
              <div className="asset-title-modern">{nft.name}</div>
              <div className="asset-detail-modern">
                {nft.itemType === "car"
                  ? `VIN: ${nft.vin}`
                  : `Address: ${nft.addr}`}
              </div>
              <div className="asset-id-modern">
                NFT ID: {nft.tokenId}
              </div>
            </div>
          ))}
        </div>
        {/* Trade offer form */}
        <form
          onSubmit={handleCreateOffer}
          style={{
            margin: "0 auto",
            maxWidth: 340,
            display: "flex",
            flexDirection: "column",
            gap: 11,
            background: "#fff",
            padding: "25px 25px 18px 25px",
            borderRadius: "16px",
            boxShadow: "0 3px 18px #4f46e512",
          }}
        >
          <label htmlFor="to" style={{ fontWeight: 600, marginBottom: 2 }}>
            Recipient Wallet Address
          </label>
          <input
            id="to"
            className="input-modern"
            type="text"
            placeholder="0x..."
            value={to}
            onChange={(e) => setTo(e.target.value)}
          />
          <label htmlFor="amount" style={{ fontWeight: 600, marginBottom: 2 }}>
            Amount (ETH)
          </label>
          <input
            id="amount"
            className="input-modern"
            type="number"
            step="0.0001"
            min="0"
            placeholder="Amount in ETH"
            value={amount}
            onChange={(e) => setAmount(e.target.value)}
          />
          <button className="button-modern" type="submit" disabled={loading}>
            {loading ? "Processing..." : "Create Offer"}
          </button>
        </form>
        {/* Pending Offers */}
        <div className="pending-box-modern">
          <div className="section-title">Pending Offers To You</div>
          <div style={{ color: "#777", fontSize: 15, marginBottom: 10 }}>
            If someone offered you an asset for ETH, you'll see it here.
          </div>
          <div className="assets-flex">
            {offers.length === 0 && <span>No pending offers.</span>}
            {offers.map((offer) => (
              <div
                key={offer.offerId}
                className="pending-offer-card"
              >
                <div className="pending-title">Offer for NFT #{offer.tokenId}</div>
                <div className="pending-detail">
                  <span style={{ color: "#0052cc", fontWeight: 600 }}>Price:</span> {offer.price} ETH
                </div>
                <div style={{ fontSize: 13 }}>
                  From: <span style={{ color: "#555" }}>{offer.seller.slice(0, 8)}...{offer.seller.slice(-4)}</span>
                </div>
                <button
                  className="pending-btn"
                  onClick={() => handleAcceptOffer(offer)}
                  disabled={loading}
                >
                  Accept & Pay
                </button>
              </div>
            ))}
          </div>
        </div>
        {loading && (
          <div className="transfer-spinner">
            <div className="spinner"></div>
          </div>
        )}
        {message && (
          <div
            className={"transfer-message" + (message.startsWith("Error") ? " error" : "")}
            style={{marginTop:16, fontWeight:500, fontSize:16}}
          >
            {message}
          </div>
        )}
      </div>
    </div>
  );
}
