import React, { useState } from "react";
import { DEMO_ACCOUNTS } from "../App";

export default function Login({ onLogin }) {
  const [cnp, setCnp] = useState("");
  const [password, setPassword] = useState("");
  const [address, setAddress] = useState("");
  const [privateKey, setPrivateKey] = useState("");
  const [mode, setMode] = useState("login"); // or "register"
  const [error, setError] = useState("");
  const [info, setInfo] = useState("");

  // Simple user DB in localStorage for demo
  function getUsers() {
    return JSON.parse(localStorage.getItem("users") || "{}");
  }
  function saveUser(cnp, password, address, privateKey) {
    const users = getUsers();
    users[cnp] = { password, address, privateKey };
    localStorage.setItem("users", JSON.stringify(users));
  }

  function handleLogin(e) {
    e.preventDefault();
    setError("");
    const users = getUsers();
    if (!users[cnp]) return setError("User not found.");
    if (users[cnp].password !== password) return setError("Incorrect password.");
    onLogin({ cnp, address: users[cnp].address, privateKey: users[cnp].privateKey });
  }

  function handleRegister(e) {
    e.preventDefault();
    setError("");
    setInfo("");
    if (!cnp.match(/^\d{13}$/)) return setError("CNP must be 13 digits.");
    if (!/^0x[a-fA-F0-9]{40}$/.test(address)) return setError("Wallet address must be 0x + 40 hex chars.");
    if (!/^0x[a-fA-F0-9]{64}$/.test(privateKey)) return setError("Private key must be 0x + 64 hex chars.");
    if (!(address.toLowerCase() in DEMO_ACCOUNTS)) return setError("Address must be a demo Hardhat account.");
    if (DEMO_ACCOUNTS[address.toLowerCase()] !== privateKey) return setError("Private key doesn't match address.");
    // (Could check uniqueness of CNP/address for real app)
    saveUser(cnp, password, address, privateKey);
    setInfo("Account created! You can now log in.");
    setMode("login");
    setCnp(""); setPassword(""); setAddress(""); setPrivateKey("");
  }

  return (
    <div style={{ minHeight: "100vh", background: "#f8f9fa", display: "flex", alignItems: "center", justifyContent: "center" }}>
      <div className="main-content" style={{ maxWidth: 430, margin: "80px auto", boxShadow: "0 8px 24px #0001", borderRadius: 18, background: "#fff" }}>
        <form onSubmit={mode === "login" ? handleLogin : handleRegister} style={{ display: "flex", flexDirection: "column", gap: 17, padding: 36 }}>
          <h2 style={{ margin: 0 }}>{mode === "login" ? "Log in" : "Create Account"}</h2>
          <div style={{ color: "#888", fontSize: 15, marginBottom: 18 }}>
            {mode === "login" ? "Connect your wallet to continue." : "Fill in details with a Hardhat demo account."}
          </div>
          <div>
            <label htmlFor="cnp">CNP</label>
            <input id="cnp" type="text" placeholder="Enter your CNP" value={cnp} onChange={e => setCnp(e.target.value)} style={{ width: "100%" }} required autoComplete="username" />
          </div>
          <div>
            <label htmlFor="pw">Password</label>
            <input id="pw" type="password" placeholder="Enter your password" value={password} onChange={e => setPassword(e.target.value)} style={{ width: "100%" }} required autoComplete="current-password" />
          </div>
          {mode === "register" && (
            <>
              <div>
                <label htmlFor="address">Wallet Address</label>
                <input id="address" type="text" placeholder="0x..." value={address} onChange={e => setAddress(e.target.value)} style={{ width: "100%" }} required />
              </div>
              <div>
                <label htmlFor="privateKey">Private Key</label>
                <input id="privateKey" type="text" placeholder="0x..." value={privateKey} onChange={e => setPrivateKey(e.target.value)} style={{ width: "100%" }} required />
              </div>
            </>
          )}
          <button className="button-main" type="submit" style={{ marginTop: 10, width: "100%" }}>
            {mode === "login" ? "Log in" : "Create Account"}
          </button>
          {error && <div style={{ color: "red", marginTop: 10 }}>{error}</div>}
          {info && <div style={{ color: "green", marginTop: 10 }}>{info}</div>}
          <button type="button" onClick={() => setMode(mode === "login" ? "register" : "login")} style={{
            marginTop: 18, border: "none", background: "none", color: "#4f46e5", fontWeight: 600, cursor: "pointer", fontSize: 15
          }}>
            {mode === "login" ? "Create Account" : "Back to Login"}
          </button>
        </form>
      </div>
    </div>
  );
}
