import React, { useEffect, useState } from "react";
import {
  getProvider,
  getMyNFTs,
  createOffer,
  getPendingOffers,
  acceptOffer,
  getEthBalance,
} from "../contractUtils";

export default function Transfer({ user }) {
  const [address, setAddress] = useState(null);
  const [nfts, setNfts] = useState([]);
  const [selected, setSelected] = useState(null);
  const [to, setTo] = useState("");
  const [amount, setAmount] = useState("");
  const [message, setMessage] = useState("");
  const [offers, setOffers] = useState([]);
  const [loading, setLoading] = useState(false);

  useEffect(() => {
    async function fetchData() {
      const provider = await getProvider();
      const signer = provider.getSigner();
      const userAddress = await signer.getAddress();
      setAddress(userAddress);
      setNfts(await getMyNFTs(userAddress));
      setOffers(await getPendingOffers(userAddress));
    }
    fetchData();
  }, [user]);

  async function handleCreateOffer(e) {
    e.preventDefault();
    if (!selected || !to || !amount) {
      setMessage("Select an asset, recipient, and amount.");
      return;
    }
    try {
      setLoading(true);
      await createOffer(selected, to, amount);
      setMessage("Offer created! Buyer will see it in their pending offers.");
      setTo("");
      setAmount("");
      setSelected(null);
    } catch (err) {
      setMessage("Error: " + err.message);
    } finally {
      setLoading(false);
    }
  }

  async function handleAcceptOffer(offer) {
    try {
      setLoading(true);
      await acceptOffer(offer.offerId, offer.price);
      setMessage(
        `Offer accepted! You paid ${offer.price} ETH and received the item.`
      );
      setOffers(await getPendingOffers(address));
      setNfts(await getMyNFTs(address));
    } catch (err) {
      setMessage("Error accepting offer: " + err.message);
    } finally {
      setLoading(false);
    }
  }

  return (
    <div className="casino-transfer-bg">
      <div className="casino-transfer-container">
        <h1 className="casino-main-title">Send Trade Offer</h1>
        <div className="casino-transfer-desc">
          Select an asset, enter recipient wallet, and set price (ETH) to create an offer.<br />
          Recipient must accept and pay for trade to complete!
        </div>
        <div className="casino-asset-list">
          {nfts.map((nft) => (
            <div
              key={nft.tokenId}
              className={
                "casino-asset-card" +
                (selected === nft.tokenId ? " casino-selected" : "")
              }
              onClick={() => setSelected(nft.tokenId)}
            >
              <div className="casino-asset-type">
                {nft.itemType === "car" ? "üöó" : "üè†"}
              </div>
              <div className="casino-asset-title">{nft.name}</div>
              <div className="casino-asset-detail">
                {nft.itemType === "car"
                  ? `VIN: ${nft.vin}`
                  : `Address: ${nft.addr}`}
              </div>
              <div className="casino-asset-id">NFT ID: {nft.tokenId}</div>
            </div>
          ))}
        </div>
        <form onSubmit={handleCreateOffer} className="casino-transfer-form">
          <label htmlFor="to" className="casino-label">
            Recipient Wallet Address
          </label>
          <input
            id="to"
            className="casino-input"
            type="text"
            placeholder="0x..."
            value={to}
            onChange={(e) => setTo(e.target.value)}
          />
          <label htmlFor="amount" className="casino-label">
            Amount (ETH)
          </label>
          <input
            id="amount"
            className="casino-input"
            type="number"
            step="0.0001"
            min="0"
            placeholder="Amount in ETH"
            value={amount}
            onChange={(e) => setAmount(e.target.value)}
          />
          <button className="casino-button-main" type="submit" disabled={loading}>
            {loading ? "Processing..." : "Create Offer"}
          </button>
        </form>
        <div style={{ display: "flex", gap: 32, marginTop: 38 }}>
          <div style={{ flex: 1, maxWidth: 420 }}>
            <div className="section-title-casino">Pending Offers To You</div>
            <div className="casino-pending-box">
              <div style={{ color: "#777", fontSize: 15, marginBottom: 10 }}>
                If someone offered you an asset for ETH, you'll see it here.
              </div>
              {offers.length === 0 && (
                <span style={{ color: "#5a6078", fontWeight: 500 }}>
                  No pending offers.
                </span>
              )}
              {offers.map((offer) => (
                <div key={offer.offerId} className="casino-pending-card">
                  <div className="casino-pending-title">
                    Offer for NFT #{offer.tokenId}
                  </div>
                  <div className="casino-pending-detail">
                    <span style={{ color: "#b68908", fontWeight: 600 }}>Price:</span>{" "}
                    <span style={{ color: "#fbbf24", fontWeight: 800 }}>{offer.price} ETH</span>
                  </div>
                  <div style={{ fontSize: 13 }}>
                    From: <span style={{ color: "#555" }}>{offer.seller.slice(0, 8)}...{offer.seller.slice(-4)}</span>
                  </div>
                  <button
                    className="casino-pending-btn"
                    onClick={() => handleAcceptOffer(offer)}
                    disabled={loading}
                  >
                    Accept & Pay
                  </button>
                </div>
              ))}
            </div>
          </div>
        </div>
        {message && (
          <div
            className={
              "casino-transfer-message" +
              (message.startsWith("Error") ? " error" : "")
            }
          >
            {message}
          </div>
        )}
      </div>
      <style>{`
        .casino-transfer-bg {
          min-height: 100vh;
          background: radial-gradient(ellipse at 60% 0, #f9fafc 75%, #e0e7ff 100%);
          padding-bottom: 50px;
        }
        .casino-transfer-container {
          max-width: 1100px;
          margin: 44px auto 0 auto;
          padding: 0 18px;
        }
        .casino-main-title {
          font-size: 2.25rem;
          font-weight: 900;
          margin-bottom: 9px;
          color: #23126d;
          letter-spacing: 0.01em;
          text-shadow: 0 4px 18px #90cdf427, 0 1px 0 #fff;
        }
        .casino-transfer-desc {
          margin-bottom: 32px;
          color: #5a6078;
          font-size: 1.16rem;
        }
        .casino-asset-list {
          display: flex;
          flex-wrap: wrap;
          gap: 28px;
          margin-bottom: 28px;
        }
        .casino-asset-card {
          background: rgba(255,255,255,0.91);
          border-radius: 19px;
          box-shadow: 0 6px 24px #6f68f13a, 0 1.5px 9px #0001;
          padding: 22px 23px 14px 22px;
          display: flex;
          flex-direction: column;
          align-items: flex-start;
