import React from "react";
import { Link, useLocation } from "react-router-dom";

export default function Navbar({
  balance,
  onLogout,
  search,
  setSearch,
  handleSearch,
}) {
  const location = useLocation();

  return (
    <header className="navbar-bg">
      <nav className="navbar">
        <div className="navbar-logo-section">
          <span className="navbar-logo">Rares<span className="dot">.</span></span>
          <div className="navbar-menu">
            <Link to="/inventory" className={location.pathname === "/inventory" ? "navbar-link active" : "navbar-link"}>
              Inventory
            </Link>
            <Link to="/transfer" className={location.pathname === "/transfer" ? "navbar-link active" : "navbar-link"}>
              Transfer
            </Link>
            <Link to="/offers" className={location.pathname === "/offers" ? "navbar-link active" : "navbar-link"}>
              Offers
            </Link>
            <Link to="/about" className={location.pathname === "/about" ? "navbar-link active" : "navbar-link"}>
              About
            </Link>
          </div>
        </div>
        <div className="navbar-actions">
          <form className="navbar-search" onSubmit={handleSearch} autoComplete="off">
            <input
              type="text"
              placeholder="Searchâ€¦"
              value={search}
              onChange={e => setSearch(e.target.value)}
            />
            <button type="submit">
              <span style={{fontWeight:600}}>Search</span>
            </button>
          </form>
          <div className="navbar-balance-chip" title="Wallet balance">
            <span className="navbar-balance-amount">
              {balance ? parseFloat(balance).toFixed(4) : "0.0000"}
            </span>
            <span className="navbar-balance-currency">ETH</span>
          </div>
          <button className="navbar-logout" onClick={onLogout}>
            Logout
          </button>
        </div>
      </nav>
      <style>{`
        .navbar-bg {
          width: 100%;
          background: #fff;
          border-bottom: 1.5px solid #e5e7eb;
        }
        .navbar {
          display: flex;
          justify-content: space-between;
          align-items: center;
          max-width: 1150px;
          margin: 0 auto;
          padding: 16px 20px 12px 20px;
          font-family: 'Inter', system-ui, sans-serif;
        }
        .navbar-logo-section {
          display: flex;
          align-items: center;
          gap: 32px;
        }
        .navbar-logo {
          font-size: 2.2rem;
          font-weight: 700;
          color: #23283b;
          letter-spacing: 0.03em;
          line-height: 1.1;
          min-width: 100px;
          user-select: none;
        }
        .dot {
          color: #4f8cff;
          font-size: 1.8rem;
        }
        .navbar-menu {
          display: flex;
          align-items: center;
          gap: 25px;
        }
        .navbar-link {
          font-size: 1.07rem;
          font-weight: 500;
          color: #6d7280;
          text-decoration: none;
          border-bottom: 2px solid transparent;
          padding-bottom: 2px;
          transition: color .13s, border .13s;
        }
        .navbar-link.active {
          color: #226cf6;
          border-bottom: 2px solid #226cf6;
        }
        .navbar-actions {
          display: flex;
          align-items: center;
          gap: 17px;
        }
        .navbar-search {
          display: flex;
          align-items: center;
          gap: 5px;
          margin-right: 9px;
        }
        .navbar-search input {
          border: 1.1px solid #e3e7ef;
          border-radius: 6px;
          font-size: 1rem;
          padding: 7px 11px;
          background: #f7f8fa;
          transition: border .12s;
          outline: none;
        }
        .navbar-search input:focus {
          border: 1.5px solid #226cf6;
          background: #fff;
        }
        .navbar-search button {
          background: #226cf6;
          color: #fff;
          font-weight: 600;
          border: none;
          border-radius: 6px;
          font-size: 1rem;
          padding: 7px 15px;
          cursor: pointer;
          box-shadow: none;
          transition: background .16s;
        }
        .navbar-search button:hover {
          background: #1947b8;
        }
        .navbar-balance-chip {
          display: flex;
          align-items: center;
          background: #f7f8fa;
          color: #23283b;
          border-radius: 24px;
          padding: 5px 17px;
          font-size: 1.1rem;
          font-weight: 600;
          border: 1px solid #edeef1;
          gap: 5px;
          min-width: 100px;
          margin-right: 2px;
        }
        .navbar-balance-amount {
          font-weight: 700;
          letter-spacing: 0.01em;
        }
        .navbar-balance-currency {
          color: #7b8196;
          font-size: 0.99em;
          font-weight: 500;
          margin-left: 5px;
          letter-spacing: 0.03em;
        }
        .navbar-logout {
          background: none;
          border: none;
          color: #226cf6;
          font-weight: 600;
          font-size: 1.04rem;
          cursor: pointer;
          padding: 0 0 0 7px;
          transition: color .13s;
        }
        .navbar-logout:hover {
          color: #e11d48;
        }
        @media (max-width: 900px) {
          .navbar {
            flex-direction: column;
            gap: 12px;
            align-items: flex-start;
            padding: 13px 4vw 8px 4vw;
          }
          .navbar-actions { margin-top: 7px; }
        }
        @media (max-width: 650px) {
          .navbar-logo-section { flex-direction: column; gap: 7px; }
          .navbar-logo { font-size: 1.42rem; min-width: 0; }
          .navbar-actions { flex-direction: column; gap: 5px; }
          .navbar-menu { gap: 12px; }
        }
      `}</style>
    </header>
  );
}
